import React, { useEffect, useState } from 'react';

const SelectorRegiones = () => {
  const [regiones, setRegiones] = useState([]);
  const [regionSeleccionada, setRegionSeleccionada] = useState('');
  const [error, setError] = useState(null);

  const fetchRegiones = async () => {
  try {
    const res = await fetch('https://corsproxy.io/?https://apis.digital.gob.cl/dpa/regiones');
    if (!res.ok) {
      console.error('Error al obtener regiones', res.status);
      return;
    }
    const data = await res.json();
    setRegiones(data);
  } catch (err) {
    console.error('Error cargando regiones:', err);
  }
};


  useEffect(() => {
    fetchRegiones();
  }, []);

  return (
    <div>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <label>Región:</label>
      <select
        value={regionSeleccionada}
        onChange={(e) => setRegionSeleccionada(e.target.value)}
        className="border p-2 rounded"
      >
        <option value="">-- Seleccione una región --</option>
        {regiones.map((r) => (
          <option key={r.codigo} value={r.codigo}>
            {r.nombre}
          </option>
        ))}
      </select>

      <p>Región seleccionada: {regionSeleccionada}</p>
    </div>
  );
};

export default SelectorRegiones;
