import React, { useEffect, useMemo, useState } from 'react';

const Mantenciones = () => {
  const [mantenciones, setMantenciones] = useState([]);
  const [vehiculos, setVehiculos] = useState([]);
  const [tipos, setTipos] = useState([]);
  const [proveedores, setProveedores] = useState([]);

  const [form, setForm] = useState({
    vehiculo_id: '',
    tipo_id: '',
    descripcion: '',
    fecha: '',
    kilometraje: '',
    costo: '',
    proveedor_id: ''
  });

  const [editingId, setEditingId] = useState(null);
  const [formErrors, setFormErrors] = useState({});
  const [searchDate, setSearchDate] = useState('');

  // Fetch data from backend
  useEffect(() => {
    fetch('http://localhost:8000/mantenciones')
      .then(res => res.json())
      .then(setMantenciones);

    fetch('http://localhost:8000/vehiculos')
      .then(res => res.json())
      .then(setVehiculos);

    fetch('http://localhost:8000/tipos_mantencion')
      .then(res => res.json())
      .then(setTipos);

    fetch('http://localhost:8000/proveedores')
      .then(res => res.json())
      .then(setProveedores);
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
    setFormErrors(prev => ({ ...prev, [name]: '' }));
  };

  // Validar formulario según restricciones
  const validateForm = () => {
    const errors = {};
    const today = new Date().toISOString().split('T')[0];

    if (!form.vehiculo_id) errors.vehiculo_id = 'Obligatorio';
    if (!form.tipo_id) errors.tipo_id = 'Obligatorio';
    if (!form.fecha) errors.fecha = 'Obligatorio';
    else if (form.fecha > today) errors.fecha = 'La fecha no puede ser futura';
    if (!form.kilometraje) errors.kilometraje = 'Obligatorio';
    else if (isNaN(Number(form.kilometraje)) || Number(form.kilometraje) < 0) errors.kilometraje = 'Debe ser número positivo';

    // Verificar kilometraje mayor o igual al último registrado del vehículo
    if (form.vehiculo_id && form.kilometraje) {
      const kmNum = Number(form.kilometraje);
      // Buscar máximo kilometraje actual del vehículo en mantenciones
      const maxKm = Math.max(
        ...mantenciones
          .filter(m => m.vehiculo_id.toString() === form.vehiculo_id)
          .map(m => m.kilometraje),
        0
      );
      if (kmNum < maxKm) {
        errors.kilometraje = `Debe ser mayor o igual al último registrado (${maxKm})`;
      }
    }

    if (!form.costo) errors.costo = 'Obligatorio';
    else if (isNaN(Number(form.costo)) || Number(form.costo) < 0) errors.costo = 'Debe ser número positivo';

    if (!form.proveedor_id) errors.proveedor_id = 'Obligatorio';

    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    if (!window.confirm(editingId ? "¿Confirma guardar los cambios?" : "¿Confirma registrar la mantención?")) return;

    const payload = {
      ...form,
      vehiculo_id: Number(form.vehiculo_id),
      tipo_id: Number(form.tipo_id),
      proveedor_id: Number(form.proveedor_id),
      kilometraje: Number(form.kilometraje),
      costo: Number(form.costo)
    };

    try {
      const res = await fetch(
        editingId
          ? `http://localhost:8000/mantenciones/${editingId}`
          : 'http://localhost:8000/mantenciones',
        {
          method: editingId ? 'PUT' : 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload)
        }
      );

      if (res.ok) {
        const data = await res.json();
        // Refrescar mantenciones
        const updatedMantenciones = editingId
          ? mantenciones.map(m => (m.id === editingId ? data : m))
          : [...mantenciones, data];
        setMantenciones(updatedMantenciones);
        resetForm();
      } else {
        alert('Error al guardar mantención');
      }
    } catch {
      alert('Error de conexión con el servidor');
    }
  };

  const handleEdit = (m) => {
    setForm({
      vehiculo_id: m.vehiculo_id.toString(),
      tipo_id: m.tipo_id.toString(),
      descripcion: m.descripcion || '',
      fecha: m.fecha,
      kilometraje: m.kilometraje.toString(),
      costo: m.costo.toString(),
      proveedor_id: m.proveedor_id.toString()
    });
    setEditingId(m.id);
    setFormErrors({});
  };

  const resetForm = () => {
    setForm({
      vehiculo_id: '',
      tipo_id: '',
      descripcion: '',
      fecha: '',
      kilometraje: '',
      costo: '',
      proveedor_id: ''
    });
    setEditingId(null);
    setFormErrors({});
  };

  const filteredMantenciones = useMemo(() => {
    if (!searchDate) return mantenciones;
    return mantenciones.filter(m => m.fecha === searchDate);
  }, [mantenciones, searchDate]);

  return (
    <div className="max-w-6xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">Mantenciones</h1>

      {/* FILTRO POR FECHA */}
      <div className="mb-6">
        <label className="mr-2 font-semibold">Filtrar por fecha:</label>
        <input
          type="date"
          value={searchDate}
          onChange={e => setSearchDate(e.target.value)}
          className="border rounded p-2"
        />
        {searchDate && (
          <button
            onClick={() => setSearchDate('')}
            className="ml-3 text-red-600 underline"
          >
            Limpiar filtro
          </button>
        )}
      </div>

      {/* FORMULARIO */}
      <form onSubmit={handleSubmit} className="mb-10 p-4 border rounded shadow">
        <h2 className="text-xl font-semibold mb-4">{editingId ? 'Editar mantención' : 'Registrar mantención'}</h2>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {/* Vehículo */}
          <div>
            <label className="block font-semibold mb-1">Vehículo *</label>
            <select name="vehiculo_id" value={form.vehiculo_id} onChange={handleChange} className="w-full border rounded p-2">
              <option value="">Seleccionar</option>
              {vehiculos.map(v => (
                <option key={v.id} value={v.id}>{v.patente}</option>
              ))}
            </select>
            {formErrors.vehiculo_id && <p className="text-red-600 text-sm">{formErrors.vehiculo_id}</p>}
          </div>

          {/* Tipo */}
          <div>
            <label className="block font-semibold mb-1">Tipo de mantención *</label>
            <select name="tipo_id" value={form.tipo_id} onChange={handleChange} className="w-full border rounded p-2">
              <option value="">Seleccionar</option>
              {tipos.map(t => (
                <option key={t.id} value={t.id}>{t.nombre}</option>
              ))}
            </select>
            {formErrors.tipo_id && <p className="text-red-600 text-sm">{formErrors.tipo_id}</p>}
          </div>

          {/* Fecha */}
          <div>
            <label className="block font-semibold mb-1">Fecha *</label>
            <input type="date" name="fecha" value={form.fecha} onChange={handleChange} className="w-full border rounded p-2" />
            {formErrors.fecha && <p className="text-red-600 text-sm">{formErrors.fecha}</p>}
          </div>

          {/* Descripción */}
          <div className="md:col-span-2">
            <label className="block font-semibold mb-1">Descripción</label>
            <input type="text" name="descripcion" value={form.descripcion} onChange={handleChange} className="w-full border rounded p-2" />
          </div>

          {/* Kilometraje */}
          <div>
            <label className="block font-semibold mb-1">Kilometraje *</label>
            <input type="number" name="kilometraje" value={form.kilometraje} onChange={handleChange} className="w-full border rounded p-2" min="0" />
            {formErrors.kilometraje && <p className="text-red-600 text-sm">{formErrors.kilometraje}</p>}
          </div>

          {/* Costo */}
          <div>
            <label className="block font-semibold mb-1">Costo *</label>
            <input type="number" name="costo" value={form.costo} onChange={handleChange} className="w-full border rounded p-2" min="0" step="0.01" />
            {formErrors.costo && <p className="text-red-600 text-sm">{formErrors.costo}</p>}
          </div>

          {/* Proveedor */}
          <div>
            <label className="block font-semibold mb-1">Proveedor *</label>
            <select name="proveedor_id" value={form.proveedor_id} onChange={handleChange} className="w-full border rounded p-2">
              <option value="">Seleccionar</option>
              {proveedores.map(p => (
                <option key={p.id} value={p.id}>{p.nombre}</option>
              ))}
            </select>
            {formErrors.proveedor_id && <p className="text-red-600 text-sm">{formErrors.proveedor_id}</p>}
          </div>
        </div>

        <div className="mt-6 flex space-x-4">
          <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
            {editingId ? 'Guardar Cambios' : 'Registrar'}
          </button>
          <button type="button" onClick={resetForm} className="bg-gray-300 px-4 py-2 rounded hover:bg-gray-400">
            Cancelar
          </button>
        </div>
      </form>

      {/* LISTADO DE MANTENCIONES */}
      <table className="w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="border border-gray-300 p-2">Vehículo</th>
            <th className="border border-gray-300 p-2">Tipo</th>
            <th className="border border-gray-300 p-2">Fecha</th>
            <th className="border border-gray-300 p-2">Descripción</th>
            <th className="border border-gray-300 p-2">Kilometraje</th>
            <th className="border border-gray-300 p-2">Costo</th>
            <th className="border border-gray-300 p-2">Proveedor</th>
            <th className="border border-gray-300 p-2">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {filteredMantenciones.length === 0 ? (
            <tr>
              <td colSpan="8" className="text-center p-4">No hay mantenciones para esta fecha.</td>
            </tr>
          ) : (
            filteredMantenciones.map(m => {
              const vehiculo = vehiculos.find(v => v.id === m.vehiculo_id);
              const tipo = tipos.find(t => t.id === m.tipo_id);
              const proveedor = proveedores.find(p => p.id === m.proveedor_id);

              return (
                <tr key={m.id} className="hover:bg-gray-50">
                  <td className="border p-2">{vehiculo?.patente || 'N/A'}</td>
                  <td className="border p-2">{tipo?.nombre || 'N/A'}</td>
                  <td className="border p-2">{m.fecha}</td>
                  <td className="border p-2">{m.descripcion}</td>
                  <td className="border p-2">{m.kilometraje}</td>
                  <td className="border p-2">${m.costo.toFixed(2)}</td>
                  <td className="border p-2">{proveedor?.nombre || 'N/A'}</td>
                  <td className="border p-2 space-x-2">
                    <button
                      onClick={() => handleEdit(m)}
                      className="text-blue-600 hover:underline"
                    >
                      Editar
                    </button>
                  </td>
                </tr>
              );
            })
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Mantenciones;
