import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const currentYear = new Date().getFullYear();

const Vehiculos = () => {
  const [vehicles, setVehicles] = useState([]);
  const [search, setSearch] = useState('');
  const [form, setForm] = useState({ patente: '', marca: '', modelo: '', anio: '', kilometraje: '' });
  const [editingId, setEditingId] = useState(null);
  const [originalKilometraje, setOriginalKilometraje] = useState(null);

  const navigate = useNavigate();

  const fetchVehicles = () => {
    fetch('http://localhost:8000/vehiculos/')
      .then(res => res.json())
      .then(data => setVehicles(data))
      .catch(err => console.error('Error cargando vehículos:', err));
  };

  useEffect(() => {
    fetchVehicles();
  }, []);

  const filteredVehicles = vehicles.filter(vehicle =>
    vehicle.patente.toLowerCase().includes(search.toLowerCase())
  );

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { patente, marca, modelo, anio, kilometraje } = form;

    if (!patente || !marca || !modelo || !anio || !kilometraje) {
      alert('Todos los campos son obligatorios.');
      return;
    }

    const parsedAnio = parseInt(anio);
    const parsedKm = parseFloat(kilometraje);

    if (isNaN(parsedAnio) || parsedAnio < 1981 || parsedAnio > currentYear) {
      alert(`El año debe estar entre 1981 y ${currentYear}`);
      return;
    }

    if (isNaN(parsedKm) || parsedKm < 0) {
      alert('El kilometraje debe ser un número positivo.');
      return;
    }

    if (editingId !== null) {
      if (parsedKm < originalKilometraje) {
        alert('No se puede ingresar un kilometraje menor al actual.');
        return;
      }

      const confirmEdit = window.confirm('¿Deseas guardar los cambios en el kilometraje?');
      if (!confirmEdit) return;
    }

    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8000/vehiculos/${editingId}`
      : 'http://localhost:8000/vehiculos/';

    const res = await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ patente, marca, modelo, anio, kilometraje }),
    });

    if (res.ok) {
      fetchVehicles();
      setForm({ patente: '', marca: '', modelo: '', anio: '', kilometraje: '' });
      setEditingId(null);
      setOriginalKilometraje(null);
    } else if (res.status === 409) {
      alert('Ya existe un vehículo con esa patente.');
    } else {
      alert('Error al guardar el vehículo.');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('¿Estás seguro de eliminar este vehículo?')) return;

    const res = await fetch(`http://localhost:8000/vehiculos/${id}`, {
      method: 'DELETE',
    });

    if (res.ok) {
      fetchVehicles();
    } else if (res.status === 400 || res.status === 403) {
      alert('No se puede eliminar el vehículo porque tiene mantenciones asociadas.');
    } else {
      alert('Error al eliminar vehículo.');
    }
  };

  const handleEdit = (vehiculo) => {
    setForm({
      patente: vehiculo.patente,
      marca: vehiculo.marca,
      modelo: vehiculo.modelo,
      anio: vehiculo.anio,
      kilometraje: vehiculo.kilometraje,
    });
    setEditingId(vehiculo.id);
    setOriginalKilometraje(parseFloat(vehiculo.kilometraje));
  };

  return (
    <div className="p-6 w-full max-w-5xl mx-auto relative">
      <button
        onClick={() => navigate(-1)}
        className="absolute top-4 left-2 bg-gray-400 hover:bg-gray-500 text-white px-2 py-2 rounded"
      >
        ← Volver al inicio
      </button>

      <h2 className="text-2xl font-bold mb-4">🚗 Vehículos</h2>

      <div className="relative mb-4">
        <input
          type="text"
          placeholder="Buscar por patente..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="p-2 pl-10 border rounded w-full"
        />
        <span className="absolute left-3 top-2.5 text-gray-500">🔍</span>
      </div>

      <form onSubmit={handleSubmit} className="mb-6 bg-white p-4 rounded shadow">
        <h3 className="text-lg font-semibold mb-2">{editingId ? 'Editar vehículo' : 'Agregar nuevo vehículo'}</h3>
        <div className="grid grid-cols-1 sm:grid-cols-5 gap-4">
          <input
            type="text"
            name="patente"
            placeholder="Patente"
            value={form.patente}
            onChange={handleChange}
            className="border p-2 rounded"
            disabled={editingId !== null} // Evita editar la patente
          />
          <input
            type="text"
            name="marca"
            placeholder="Marca"
            value={form.marca}
            onChange={handleChange}
            className="border p-2 rounded"
            disabled={editingId !== null} // Solo se edita kilometraje
          />
          <input
            type="text"
            name="modelo"
            placeholder="Modelo"
            value={form.modelo}
            onChange={handleChange}
            className="border p-2 rounded"
            disabled={editingId !== null}
          />
          <input
            type="number"
            name="anio"
            placeholder="Año"
            value={form.anio}
            onChange={handleChange}
            className="border p-2 rounded"
            disabled={editingId !== null}
          />
          <input
            type="number"
            name="kilometraje"
            placeholder="Kilometraje"
            value={form.kilometraje}
            onChange={handleChange}
            className="border p-2 rounded"
          />
        </div>
        <button type="submit" className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          {editingId ? 'Guardar cambios' : 'Agregar vehículo'}
        </button>
      </form>

      {filteredVehicles.length === 0 ? (
        <p>No hay vehículos registrados.</p>
      ) : (
        <ul className="space-y-2">
          {filteredVehicles.map(vehicle => (
            <li key={vehicle.id} className="bg-white p-4 rounded shadow flex justify-between items-center">
              <div>
                <strong>{vehicle.patente}</strong> - {vehicle.modelo} - {vehicle.marca} - Año {vehicle.anio} - {vehicle.kilometraje} km
              </div>
              <div className="space-x-2">
                <button
                  onClick={() => handleEdit(vehicle)}
                  className="bg-yellow-400 text-white px-3 py-1 rounded hover:bg-yellow-500"
                >
                  Editar
                </button>
                <button
                  onClick={() => handleDelete(vehicle.id)}
                  className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                >
                  Eliminar
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Vehiculos;
