import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Proveedores = () => {
  const [proveedores, setProveedores] = useState([]);
  const [direcciones, setDirecciones] = useState([]);
  const [regiones, setRegiones] = useState([]);
  const [comunas, setComunas] = useState([]);

  const [search, setSearch] = useState('');
  const [form, setForm] = useState({
    nombre: '',
    telefono: '',
    email: '',
    direccion_id: '',
    usarNuevaDireccion: false,
    nuevaCalle: '',
    nuevaRegion: '',
    nuevaComuna: '',
  });
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState(null);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);

  const [alerta, setAlerta] = useState(null); // { tipo: 'success'|'error', mensaje: string }

  const navigate = useNavigate();

  // Cargar proveedores
  const fetchProveedores = async () => {
    try {
      const res = await fetch('http://localhost:8000/proveedores/');
      if (!res.ok) {
        console.error('Error al obtener proveedores', res.status);
        return;
      }
      const data = await res.json();
      setProveedores(data);
    } catch (err) {
      console.error('Error cargando proveedores:', err);
    }
  };

  // Cargar direcciones existentes
  const fetchDirecciones = async () => {
    try {
      const res = await fetch('http://localhost:8000/direcciones/');
      if (!res.ok) {
        console.error('Error al obtener direcciones', res.status);
        return;
      }
      const data = await res.json();
      setDirecciones(data);
    } catch (err) {
      console.error('Error cargando direcciones:', err);
    }
  };

  // Cargar regiones usando proxy para evitar CORS
  const fetchRegiones = async () => {
    try {
      const res = await fetch('https://corsproxy.io/?https://apis.digital.gob.cl/dpa/regiones');
      if (!res.ok) {
        console.error('Error al obtener regiones', res.status);
        return;
      }
      const data = await res.json();
      setRegiones(data);
    } catch (err) {
      console.error('Error cargando regiones:', err);
    }
  };

  // Cargar comunas seg√∫n la regi√≥n seleccionada (tambi√©n via proxy)
  const fetchComunas = async (regionCodigo) => {
    try {
      if (!regionCodigo) {
        setComunas([]);
        return;
      }
      const res = await fetch(
        `https://corsproxy.io/?https://apis.digital.gob.cl/dpa/regiones/${regionCodigo}/comunas`
      );
      if (!res.ok) {
        console.error('Error al obtener comunas', res.status);
        setComunas([]);
        return;
      }
      const data = await res.json();
      setComunas(data);
    } catch (err) {
      console.error('Error cargando comunas:', err);
      setComunas([]);
    }
  };

  // Carga inicial
  useEffect(() => {
    fetchProveedores();
    fetchDirecciones();
    fetchRegiones();
  }, []);

  // Manejo de cambios en formulario
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name === 'telefono') {
      let val = value;
      if (val.startsWith('+56')) {
        val = val.slice(3);
      }
      val = val.replace(/\D/g, '').slice(0, 9);
      setForm(prev => ({ ...prev, telefono: val }));
      setError(null);
      return;
    }

    if (type === 'checkbox') {
      setForm(prev => ({ ...prev, [name]: checked }));
      if (name === 'usarNuevaDireccion' && !checked) {
        setForm(prev => ({
          ...prev,
          nuevaCalle: '',
          nuevaRegion: '',
          nuevaComuna: '',
        }));
        setComunas([]);
      }
    } else {
      // Ahora el cambio de nuevaRegion no se maneja aqu√≠ para evitar llamada doble a fetchComunas
      setForm(prev => ({ ...prev, [name]: value }));
    }
    setError(null);
  };

  // Guardar o editar proveedor
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.nombre || !form.telefono || !form.email) {
      setError('Por favor completa nombre, tel√©fono y correo.');
      return;
    }

    if (form.telefono.length !== 9) {
      setError('El tel√©fono debe tener 9 d√≠gitos despu√©s del +56.');
      return;
    }

    if (form.usarNuevaDireccion) {
      if (!form.nuevaCalle || !form.nuevaRegion || !form.nuevaComuna) {
        setError('Por favor completa todos los datos de la nueva direcci√≥n.');
        return;
      }
    } else {
      if (!form.direccion_id) {
        setError('Selecciona una direcci√≥n existente o crea una nueva.');
        return;
      }
    }

    const nombreLower = form.nombre.trim().toLowerCase();
    const nombreExiste = proveedores.some(p =>
      p.nombre.trim().toLowerCase() === nombreLower &&
      p.id !== editingId
    );
    if (nombreExiste) {
      setError('Ya existe un proveedor con ese nombre.');
      return;
    }

    let body = {
      nombre: form.nombre,
      telefono: '+56' + form.telefono,
      email: form.email,
    };

    if (form.usarNuevaDireccion) {
      body = {
        ...body,
        direccion_nueva: {
          calle: form.nuevaCalle,
          region: form.nuevaRegion,
          comuna: form.nuevaComuna,
        },
      };
    } else {
      body = {
        ...body,
        direccion_id: form.direccion_id,
      };
    }

    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8000/proveedores/${editingId}`
      : 'http://localhost:8000/proveedores/';

    try {
      const res = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body),
      });
      if (res.ok) {
        fetchProveedores();
        setForm({
          nombre: '',
          telefono: '',
          email: '',
          direccion_id: '',
          usarNuevaDireccion: false,
          nuevaCalle: '',
          nuevaRegion: '',
          nuevaComuna: '',
        });
        setComunas([]);
        setEditingId(null);
        setError(null);
        setMostrarFormulario(false);
        setAlerta({ tipo: 'success', mensaje: editingId ? 'Proveedor actualizado correctamente.' : 'Proveedor creado correctamente.' });
      } else {
        const data = await res.json();
        setError(data.message || `Error al guardar proveedor (status ${res.status})`);
      }
    } catch (err) {
      console.error(err);
      setError('Error de conexi√≥n al guardar proveedor');
    }
  };

  // Editar proveedor
  const handleEdit = (p) => {
    let telefonoSinPrefijo = p.telefono || '';
    if (telefonoSinPrefijo.startsWith('+56')) {
      telefonoSinPrefijo = telefonoSinPrefijo.slice(3);
    }
    setForm({
      nombre: p.nombre || '',
      telefono: telefonoSinPrefijo,
      email: p.email || '',
      direccion_id: p.direccion?.id || '',
      usarNuevaDireccion: false,
      nuevaCalle: '',
      nuevaRegion: '',
      nuevaComuna: '',
    });
    setComunas([]);
    setEditingId(p.id);
    setError(null);
    setMostrarFormulario(true);
  };

  // Eliminar proveedor
  const handleDelete = async (id) => {
    if (!window.confirm('¬øEst√°s seguro de eliminar este proveedor?')) return;
    try {
      const res = await fetch(`http://localhost:8000/proveedores/${id}`, {
        method: 'DELETE',
      });
      if (res.ok) {
        fetchProveedores();
        setAlerta({ tipo: 'success', mensaje: 'Proveedor eliminado correctamente.' });
      } else {
        const data = await res.json();
        if (data.code === 'DEPENDENCY_ERROR') {
          alert('No se puede eliminar el proveedor porque tiene mantenciones activas.');
        } else {
          alert('Error al eliminar proveedor');
        }
      }
    } catch (err) {
      alert('Error de conexi√≥n al eliminar proveedor');
    }
  };

  const filteredProveedores = proveedores.filter(p =>
    p.nombre.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="p-6 w-full max-w-5xl mx-auto relative">

      {/* Mensaje de alerta */}
      {alerta && (
        <div
          className={`fixed top-4 right-4 z-50 max-w-xs px-5 py-3 rounded shadow-lg text-white flex justify-between items-center
            ${alerta.tipo === 'success' ? 'bg-green-500 text-white' : 'bg-red-500 text-white'}`}
          role="alert"
        >
          <span>{alerta.mensaje}</span>
          <button
            onClick={() => setAlerta(null)}
            className="ml-4 font-bold text-xl leading-none focus:outline-none"
            aria-label="Cerrar alerta"
          >
            √ó
          </button>
        </div>
      )}

      <button
        onClick={() => navigate(-1)}
        className="absolute top-4 left-4 bg-gray-400 hover:bg-gray-500 text-white px-3 py-2 rounded"
      >
        ‚Üê Volver al inicio
      </button>

      <h2 className="text-2xl font-bold mb-6 text-center">üè≠ Proveedores</h2>

      {!mostrarFormulario && (
        <button
          onClick={() => {
            setMostrarFormulario(true);
            setEditingId(null);
            setForm({
              nombre: '',
              telefono: '',
              email: '',
              direccion_id: '',
              usarNuevaDireccion: false,
              nuevaCalle: '',
              nuevaRegion: '',
              nuevaComuna: '',
            });
            setComunas([]);
            setError(null);
          }}
          className="mb-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          + Agregar nuevo proveedor
        </button>
      )}

      <div className="relative mb-4">
        <input
          type="text"
          placeholder="üîç Buscar por nombre..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="p-2 pl-10 border rounded w-full"
        />
      </div>

      {mostrarFormulario && (
        <form onSubmit={handleSubmit} className="mb-6 bg-white p-4 rounded shadow">
          <h3 className="text-lg font-semibold mb-2">
            {editingId ? 'Editar proveedor' : 'Agregar nuevo proveedor'}
          </h3>

          {error && <p className="text-red-600 mb-2">{error}</p>}

          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
            <input
              type="text"
              name="nombre"
              placeholder="Nombre *"
              value={form.nombre}
              onChange={handleChange}
              className="border p-2 rounded"
              required
            />
            <input
              type="text"
              name="telefono"
              placeholder="Tel√©fono *"
              value={'+56' + form.telefono}
              onChange={handleChange}
              className="border p-2 rounded"
              maxLength={12} // +56 + 9 d√≠gitos
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email *"
              value={form.email}
              onChange={handleChange}
              className="border p-2 rounded"
              required
            />

            {!form.usarNuevaDireccion && (
              <select
                name="direccion_id"
                value={form.direccion_id}
                onChange={handleChange}
                className="border p-2 rounded"
                required={!form.usarNuevaDireccion}
              >
                <option value="">Seleccione direcci√≥n existente</option>
                {direcciones.map(d => (
                  <option key={d.id} value={d.id}>
                    {`${d.calle}, ${d.comuna}, ${d.region}`}
                  </option>
                ))}
              </select>
            )}

            <div className="flex items-center">
              <input
                type="checkbox"
                name="usarNuevaDireccion"
                checked={form.usarNuevaDireccion}
                onChange={handleChange}
                className="mr-2"
              />
              <label>Crear nueva direcci√≥n</label>
            </div>
          </div>

          {form.usarNuevaDireccion && (
            <div className="mt-4 bg-gray-100 p-4 rounded">
              <h4 className="font-medium mb-2">Datos nueva direcci√≥n</h4>
              <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
                <input
                  type="text"
                  name="nuevaCalle"
                  placeholder="Calle *"
                  value={form.nuevaCalle}
                  onChange={handleChange}
                  className="border p-2 rounded"
                  required={form.usarNuevaDireccion}
                />
                <select
                  name="nuevaRegion"
                  value={form.nuevaRegion}
                  onChange={(e) => {
                    const regionCodigo = e.target.value;
                    setForm(prev => ({
                      ...prev,
                      nuevaRegion: regionCodigo,
                      nuevaComuna: '',
                    }));
                    fetchComunas(regionCodigo);
                  }}
                  className="border p-2 rounded"
                  required={form.usarNuevaDireccion}
                >
                  <option value="">Seleccione Regi√≥n *</option>
                  {regiones.map(r => (
                    <option key={r.codigo} value={r.codigo}>
                      {r.nombre}
                    </option>
                  ))}
                </select>
                <select
                  name="nuevaComuna"
                  value={form.nuevaComuna}
                  onChange={handleChange}
                  className="border p-2 rounded"
                  required={form.usarNuevaDireccion}
                  disabled={!form.nuevaRegion}
                >
                  <option value="">Seleccione Comuna *</option>
                  {comunas.map(c => (
                    <option key={c.nombre} value={c.nombre}>
                      {c.nombre}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          )}

          <div className="mt-4 flex space-x-2">
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              {editingId ? 'Actualizar' : 'Guardar'}
            </button>
            <button
              type="button"
              onClick={() => {
                setMostrarFormulario(false);
                setEditingId(null);
                setForm({
                  nombre: '',
                  telefono: '',
                  email: '',
                  direccion_id: '',
                  usarNuevaDireccion: false,
                  nuevaCalle: '',
                  nuevaRegion: '',
                  nuevaComuna: '',
                });
                setComunas([]);
                setError(null);
              }}
              className="bg-gray-400 text-white px-4 py-2 rounded hover:bg-gray-500"
            >
              Cancelar
            </button>
          </div>
        </form>
      )}

      {/* Tabla de proveedores */}
      <table className="w-full table-auto border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-200">
            <th className="border border-gray-300 px-4 py-2">Nombre</th>
            <th className="border border-gray-300 px-4 py-2">Tel√©fono</th>
            <th className="border border-gray-300 px-4 py-2">Email</th>
            <th className="border border-gray-300 px-4 py-2">Direcci√≥n</th>
            <th className="border border-gray-300 px-4 py-2">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {filteredProveedores.map(p => (
            <tr key={p.id} className="hover:bg-gray-100">
              <td className="border border-gray-300 px-4 py-2">{p.nombre}</td>
              <td className="border border-gray-300 px-4 py-2">{p.telefono}</td>
              <td className="border border-gray-300 px-4 py-2">{p.email}</td>
              <td className="border border-gray-300 px-4 py-2">
                {p.direccion
                  ? `${p.direccion.calle}, ${p.direccion.comuna}, ${p.direccion.region}`
                  : 'Sin direcci√≥n'}
              </td>
              <td className="border border-gray-300 px-4 py-2">
                <button
                  onClick={() => handleEdit(p)}
                  className="mr-2 bg-yellow-400 hover:bg-yellow-500 text-white px-2 py-1 rounded"
                >
                  Editar
                </button>
                <button
                  onClick={() => handleDelete(p.id)}
                  className="bg-red-600 hover:bg-red-700 text-white px-2 py-1 rounded"
                >
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
          {filteredProveedores.length === 0 && (
            <tr>
              <td colSpan="5" className="text-center p-4 text-gray-600">
                No hay proveedores para mostrar.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Proveedores;
