import React, { useEffect, useState } from 'react';

const SelectorRegionesComunas = () => {
  const [regiones, setRegiones] = useState([]);
  const [comunas, setComunas] = useState([]);

  const [regionSeleccionada, setRegionSeleccionada] = useState('');
  const [comunaSeleccionada, setComunaSeleccionada] = useState('');

  const [error, setError] = useState(null);

  // Obtener regiones desde la API p煤blica
  const fetchRegiones = async () => {
    try {
      const res = await fetch('https://corsproxy.io/?https://apis.digital.gob.cl/dpa/regiones');
      if (!res.ok) {
        setError('Error al obtener regiones');
        return;
      }
      const data = await res.json();
      setRegiones(data);
    } catch (err) {
      console.error(err);
      setError('Error de conexi贸n al cargar regiones');
    }
  };

  // Obtener comunas seg煤n la regi贸n seleccionada
  const fetchComunas = async (regionCodigo) => {
    try {
      const res = await fetch(
        `https://corsproxy.io/?https://apis.digital.gob.cl/dpa/regiones/${regionCodigo}/comunas`
      );
      if (!res.ok) {
        setError('Error al obtener comunas');
        return;
      }
      const data = await res.json();
      setComunas(data);
    } catch (err) {
      console.error(err);
      setError('Error de conexi贸n al cargar comunas');
    }
  };

  // Cargar regiones al montar
  useEffect(() => {
    fetchRegiones();
  }, []);

  // Cuando cambia la regi贸n, cargar comunas
  useEffect(() => {
    if (regionSeleccionada) {
      fetchComunas(regionSeleccionada);
      setComunaSeleccionada(''); // reset comuna al cambiar regi贸n
    } else {
      setComunas([]);
    }
  }, [regionSeleccionada]);

  return (
    <div className="p-6 max-w-lg mx-auto bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4"> Selecciona Regi贸n y Comuna</h2>

      {error && <p className="text-red-600 mb-4">{error}</p>}

      {/* Selector de Regi贸n */}
      <label className="block mb-2 font-medium">Regi贸n:</label>
      <select
        value={regionSeleccionada}
        onChange={(e) => setRegionSeleccionada(e.target.value)}
        className="border p-2 rounded w-full mb-4"
        required
      >
        <option value="">-- Selecciona una regi贸n --</option>
        {regiones.map((region) => (
          <option key={region.codigo} value={region.codigo}>
            {region.nombre}
          </option>
        ))}
      </select>

      {/* Selector de Comuna */}
      {regionSeleccionada && (
        <>
          <label className="block mb-2 font-medium">Comuna:</label>
          <select
            value={comunaSeleccionada}
            onChange={(e) => setComunaSeleccionada(e.target.value)}
            className="border p-2 rounded w-full mb-4"
            required
          >
            <option value="">-- Selecciona una comuna --</option>
            {comunas.map((comuna) => (
              <option key={comuna.codigo} value={comuna.nombre}>
                {comuna.nombre}
              </option>
            ))}
          </select>
        </>
      )}

      {/* Mostrar selecci贸n actual */}
      {regionSeleccionada && comunaSeleccionada && (
        <div className="mt-4 text-gray-700">
          <p>
            Regi贸n seleccionada:{' '}
            <strong>
              {regiones.find((r) => r.codigo === regionSeleccionada)?.nombre}
            </strong>
          </p>
          <p>
            Comuna seleccionada: <strong>{comunaSeleccionada}</strong>
          </p>
        </div>
      )}
    </div>
  );
};

export default SelectorRegionesComunas;
