import React, { useEffect, useState } from 'react';

const SelectorRegiones = () => {
  const [regiones, setRegiones] = useState([]);
  const [regionSeleccionada, setRegionSeleccionada] = useState('');
  const [error, setError] = useState(null);

  // Cargar regiones con CORS proxy
  const fetchRegiones = async () => {
    try {
      const res = await fetch('https://corsproxy.io/?https://apis.digital.gob.cl/dpa/regiones');
      if (!res.ok) {
        setError(`Error al obtener regiones: ${res.status}`);
        return;
      }
      const data = await res.json();
      setRegiones(data);
    } catch (err) {
      setError('Error de conexión al traer regiones');
      console.error(err);
    }
  };

  useEffect(() => {
    fetchRegiones();
  }, []);

  const handleChange = (e) => {
    setRegionSeleccionada(e.target.value);
  };

  return (
    <div className="p-4 bg-white rounded shadow max-w-md mx-auto">
      <h3 className="text-lg font-semibold mb-2">🌎 Selecciona una región</h3>

      {error && <p className="text-red-600 mb-2">{error}</p>}

      <label htmlFor="region" className="block mb-1">
        Región:
      </label>
      <select
        id="region"
        value={regionSeleccionada}
        onChange={handleChange}
        className="border p-2 rounded w-full"
        required
      >
        <option value="">-- Seleccione una región --</option>
        {regiones.map((r) => (
          <option key={r.codigo} value={r.codigo}>
            {r.nombre}
          </option>
        ))}
      </select>

      {regionSeleccionada && (
        <p className="mt-3 text-gray-700">
          Región seleccionada:{" "}
          <strong>
            {regiones.find((r) => r.codigo === regionSeleccionada)?.nombre}
          </strong>
        </p>
      )}
    </div>
  );
};

export default SelectorRegiones;
