import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Mantenciones = () => {
  const [mantenciones, setMantenciones] = useState([]);
  const [vehiculos, setVehiculos] = useState([]);
  const [tipos, setTipos] = useState([]);
  const [proveedores, setProveedores] = useState([]);

  const [form, setForm] = useState({
    vehiculo_id: '',
    tipo_id: '',
    descripcion: '',
    fecha: '',
    kilometraje: '',
    costo: '',
    proveedor_id: ''
  });

  const [editingId, setEditingId] = useState(null);
  const [formErrors, setFormErrors] = useState({});
  const [searchDate, setSearchDate] = useState('');
  const [searchTipoId, setSearchTipoId] = useState('');
  const [showForm, setShowForm] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:8000/mantenciones')
      .then(res => res.json())
      .then(setMantenciones)
      .catch(err => {
        console.error('Error fetching mantenciones:', err);
        setMantenciones([]);
      });

    fetch('http://localhost:8000/vehiculos')
      .then(res => res.json())
      .then(setVehiculos)
      .catch(err => {
        console.error('Error fetching vehiculos:', err);
        setVehiculos([]);
      });

    fetch('http://localhost:8000/mantenciones/tipos_mantencion')
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setTipos(data);
        } else if (Array.isArray(data.tipos)) {
          setTipos(data.tipos);
        } else {
          console.error('Tipos mantencion no es un array:', data);
          setTipos([]);
        }
      })
      .catch(err => {
        console.error('Error fetching tipos mantencion:', err);
        setTipos([]);
      });

    fetch('http://localhost:8000/proveedores')
      .then(res => res.json())
      .then(setProveedores)
      .catch(err => {
        console.error('Error fetching proveedores:', err);
        setProveedores([]);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
    setFormErrors(prev => ({ ...prev, [name]: '' }));
  };

  const validateForm = () => {
    const errors = {};
    const today = new Date().toISOString().split('T')[0];

    if (!form.vehiculo_id) errors.vehiculo_id = 'Obligatorio';
    if (!form.tipo_id) errors.tipo_id = 'Obligatorio';
    if (!form.fecha) errors.fecha = 'Obligatorio';
    else if (form.fecha > today) errors.fecha = 'La fecha no puede ser futura';
    if (!form.kilometraje) errors.kilometraje = 'Obligatorio';
    else if (isNaN(Number(form.kilometraje)) || Number(form.kilometraje) < 0) errors.kilometraje = 'Debe ser número positivo';

    if (form.vehiculo_id && form.kilometraje) {
      const kmNum = Number(form.kilometraje);
      const maxKm = Math.max(
        ...mantenciones
          .filter(m => m.vehiculo_id.toString() === form.vehiculo_id)
          .map(m => m.kilometraje),
        0
      );
      if (kmNum < maxKm) {
        errors.kilometraje = `Debe ser mayor o igual al último registrado (${maxKm})`;
      }
    }

    if (!form.costo) errors.costo = 'Obligatorio';
    else if (isNaN(Number(form.costo)) || Number(form.costo) < 0) errors.costo = 'Debe ser número positivo';

    if (!form.proveedor_id) errors.proveedor_id = 'Obligatorio';

    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    const action = editingId ? 'editar' : 'registrar';

    if (!window.confirm(`¿Confirma ${action} la mantención?`)) return;

    const payload = {
      ...form,
      vehiculo_id: Number(form.vehiculo_id),
      tipo_id: Number(form.tipo_id),
      proveedor_id: Number(form.proveedor_id),
      kilometraje: Number(form.kilometraje),
      costo: Number(form.costo)
    };

    try {
      const res = await fetch(
        editingId
          ? `http://localhost:8000/mantenciones/${editingId}`
          : 'http://localhost:8000/mantenciones',
        {
          method: editingId ? 'PUT' : 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload)
        }
      );

      if (res.ok) {
        const data = await res.json();
        const updatedMantenciones = editingId
          ? mantenciones.map(m => (m.id === editingId ? data : m))
          : [...mantenciones, data];
        setMantenciones(updatedMantenciones);
        resetForm();
        setShowForm(false);
      } else {
        alert('Error al guardar mantención');
      }
    } catch {
      alert('Error de conexión con el servidor');
    }
  };

{
    setForm({
      vehiculo_id: m.vehiculo_id.toString(),
      tipo_id: m.tipo_id.toString(),
      descripcion: m.descripcion || '',
      fecha: m.fecha,
      kilometraje: m.kilometraje.toString(),
      costo: m.costo.toString(),
      proveedor_id: m.proveedor_id.toString()
    });
    setEditingId(m.id);
    setFormErrors({});
    setShowForm(true);
  };

  const resetForm = () => {
    setForm({
      vehiculo_id: '',
      tipo_id: '',
      descripcion: '',
      fecha: '',
      kilometraje: '',
      costo: '',
      proveedor_id: ''
    });
    setEditingId(null);
    setFormErrors({});
  };

  const handleNewVehicleClick = () => {
    setShowForm(true);
  };

  const filteredMantenciones = useMemo(() => {
    return mantenciones.filter(m => {
      const matchFecha = searchDate ? m.fecha === searchDate : true;
      const matchTipo = searchTipoId ? m.tipo_id.toString() === searchTipoId : true;
      return matchFecha && matchTipo;
    });
  }, [mantenciones, searchDate, searchTipoId]);

  const getVehiculoById = (id) => {
    return vehiculos.find(v => v.id === id);
  };

  return (
    <div className="p-6 w-full max-w-6xl mx-auto relative">
      <button
        onClick={() => navigate(-1)}
        className="absolute top-4 left-4 bg-gray-400 hover:bg-gray-500 text-white px-3 py-2 rounded"
      >
        ← Volver al inicio
      </button>

      <h2 className="text-2xl font-bold mb-6 text-center">Mantenciones</h2>

      {!showForm && (
        <button
          onClick={handleNewVehicleClick}
          className="mb-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          ➕ Agregar nueva Mantención
        </button>
      )}

      <div className="mb-6 flex flex-wrap items-center space-x-4">
        <label className="font-semibold">Filtrar por fecha:</label>
        <input
          type="date"
          value={searchDate}
          onChange={e => setSearchDate(e.target.value)}
          className="border rounded p-2"
        />
        {searchDate && (
          <button
            onClick={() => setSearchDate('')}
            className="text-red-600 underline"
          >
            Limpiar filtro
          </button>
        )}

        <label className="font-semibold">Filtrar por tipo:</label>
        <select
          value={searchTipoId}
          onChange={e => setSearchTipoId(e.target.value)}
          className="border rounded p-2"
        >
          <option value="">Seleccione un tipo</option>
          {tipos.map(t => (
            <option key={t.id} value={t.id}>
              {t.nombre}
            </option>
          ))}
        </select>
      </div>

      {/* Tabla de mantenciones con color suave */}
      <div className="overflow-x-auto">
        <table className="min-w-full border-collapse rounded-lg overflow-hidden shadow bg-blue-50">
          <thead className="bg-blue-100">
            <tr>
              <th className="border px-4 py-2">Patente</th>
              <th className="border px-4 py-2">Tipo de mantención</th>
              <th className="border px-4 py-2">Fecha</th>
              <th className="border px-4 py-2">Kilometraje</th>
              <th className="border px-4 py-2">Costo ($)</th>
              <th className="border px-4 py-2">Descripción</th>
              <th className="border px-4 py-2">Proveedor</th>
              <th className="border px-4 py-2">Acciones</th>
            </tr>
          </thead>
          <tbody>
            {filteredMantenciones.map(m => {
              const vehiculo = getVehiculoById(m.vehiculo_id);
              const tipo = tipos.find(t => t.id === m.tipo_id);
              const proveedor = proveedores.find(p => p.id === m.proveedor_id);

              return (
                <tr key={m.id} className="hover:bg-blue-100">
                  <td className="border px-4 py-2">{vehiculo?.patente || 'Desconocido'}</td>
                  <td className="border px-4 py-2">{tipo?.nombre || 'Desconocido'}</td>
                  <td className="border px-4 py-2">{m.fecha}</td>
                  <td className="border px-4 py-2">{m.kilometraje}</td>
                  <td className="border px-4 py-2">${m.costo}</td>
                  <td className="border px-4 py-2">{m.descripcion?.slice(0, 500) || 'Sin descripción'}</td>
                  <td className="border px-4 py-2">{proveedor?.nombre || 'Desconocido'}</td>
                  <td className="border px-4 py-2">{/* Intencionalmente vacío */}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {/* Formulario (puedes dejar igual) */}
      {showForm && (
        <div className="mt-6 bg-white p-6 border rounded shadow-lg">
          <h3 className="text-xl font-semibold mb-4">
            {editingId ? 'Editar Mantención' : 'Registrar Nueva Mantención'}
          </h3>
          <form onSubmit={handleSubmit}>
            <div className="grid grid-cols-2 gap-4">
              <div className="col-span-2">
                <label className="block mb-2">Vehículo:</label>
                <select
                  name="vehiculo_id"
                  value={form.vehiculo_id}
                  onChange={handleChange}
                  className="border rounded p-2 w-full"
                >
                  <option value="">Seleccione un vehículo</option>
                  {vehiculos.map(v => (
                    <option key={v.id} value={v.id}>
                      {v.patente} - {v.modelo}
                    </option>
                  ))}
                </select>
                {formErrors.vehiculo_id && <p className="text-red-600">{formErrors.vehiculo_id}</p>}
              </div>

              <div>
                <label className="block mb-2">Tipo:</label>
                <select
                  name="tipo_id"
                  value={form.tipo_id}
                  onChange={handleChange}
                  className="border rounded p-2 w-full"
                >
                  <option value="">Seleccione un tipo</option>
                  {tipos.map(t => (
                    <option key={t.id} value={t.id}>
                      {t.nombre}
                    </option>
                  ))}
                </select>
                {formErrors.tipo_id && <p className="text-red-600">{formErrors.tipo_id}</p>}
              </div>

              <div>
                <label className="block mb-2">Fecha:</label>
                <input
                  type="date"
                  name="fecha"
                  value={form.fecha}
                  onChange={handleChange}
                  className="border rounded p-2 w-full"
                />
                {formErrors.fecha && <p className="text-red-600">{formErrors.fecha}</p>}
              </div>

              <div>
                <label className="block mb-2">Kilometraje:</label>
                <input
                  type="number"
                  name="kilometraje"
                  value={form.kilometraje}
                  onChange={handleChange}
                  className="border rounded p-2 w-full"
                />
                {formErrors.kilometraje && <p className="text-red-600">{formErrors.kilometraje}</p>}
              </div>

              <div>
                <label className="block mb-2">Costo:</label>
                <input
                  type="number"
                  name="costo"
                  value={form.costo}
                  onChange={handleChange}
                  className="border rounded p-2 w-full"
                />
                {formErrors.costo && <p className="text-red-600">{formErrors.costo}</p>}
              </div>

              <div>
                <label className="block mb-2">Proveedor:</label>
                <select
                  name="proveedor_id"
                  value={form.proveedor_id}
                  onChange={handleChange}
                  className="border rounded p-2 w-full"
                >
                  <option value="">Seleccione un proveedor</option>
                  {proveedores.map(p => (
                    <option key={p.id} value={p.id}>
                      {p.nombre}
                    </option>
                  ))}
                </select>
                {formErrors.proveedor_id && <p className="text-red-600">{formErrors.proveedor_id}</p>}
              </div>

              <div className="col-span-2">
                <label className="block mb-2">Descripción:</label>
                <textarea
                  name="descripcion"
                  value={form.descripcion}
                  onChange={handleChange}
                  className="border rounded p-2 w-full"
                  rows="4"
                  maxLength="500"
                />
              </div>
            </div>

            <div className="mt-4 flex justify-end">
              <button
                type="button"
                onClick={() => setShowForm(false)}
                className="mr-2 bg-gray-400 hover:bg-gray-500 text-white px-4 py-2 rounded"
              >
                Cancelar
              </button>
              <button
                type="submit"
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
              >
                {editingId ? 'Guardar Cambios' : 'Registrar Mantención'}
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};

export default Mantenciones;
