import React, { useEffect, useState, useMemo } from 'react';

const Mantenciones = () => {
  const [mantenciones, setMantenciones] = useState([]);
  const [vehiculos, setVehiculos] = useState([]);
  const [tipos, setTipos] = useState([]);
  const [proveedores, setProveedores] = useState([]);

  const [searchDate, setSearchDate] = useState('');
  const [form, setForm] = useState({
    vehiculo_id: '',
    tipo_id: '',
    descripcion: '',
    fecha: '',
    kilometraje: '',
    costo: '',
    proveedor_id: '',
  });
  const [formErrors, setFormErrors] = useState({});
  const [editingId, setEditingId] = useState(null);
  const [originalKilometraje, setOriginalKilometraje] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');

  // Carga inicial de datos
  useEffect(() => {
    fetchAll();
  }, []);

  const fetchAll = () => {
    fetchMantenciones();
    fetchVehiculos();
    fetchTipos();
    fetchProveedores();
  };

  const fetchMantenciones = () => {
    fetch('http://localhost:8000/mantenciones')
      .then(res => res.json())
      .then(data => setMantenciones(data))
      .catch(err => console.error('Error cargando mantenciones:', err));
  };

  const fetchVehiculos = () => {
    fetch('http://localhost:8000/vehiculos')
      .then(res => res.json())
      .then(data => setVehiculos(data))
      .catch(err => console.error('Error cargando veh√≠culos:', err));
  };

  const fetchTipos = () => {
    fetch('http://localhost:8000/tipos-mantencion')
      .then(res => res.json())
      .then(data => setTipos(data))
      .catch(err => console.error('Error cargando tipos:', err));
  };

  const fetchProveedores = () => {
    fetch('http://localhost:8000/proveedores')
      .then(res => res.json())
      .then(data => setProveedores(data))
      .catch(err => console.error('Error cargando proveedores:', err));
  };

  // Filtrar mantenciones por fecha
  const filteredMantenciones = useMemo(() => {
    if (!searchDate) return mantenciones;
    return mantenciones.filter(m => m.fecha === searchDate);
  }, [mantenciones, searchDate]);

  // Validaciones formulario
  const validateForm = () => {
    const errors = {};
    const today = new Date().toISOString().split('T')[0]; // yyyy-mm-dd

    if (!form.vehiculo_id) errors.vehiculo_id = 'Seleccione un veh√≠culo';
    if (!form.tipo_id) errors.tipo_id = 'Seleccione tipo de mantenci√≥n';
    if (!form.fecha) errors.fecha = 'Ingrese fecha';
    else if (form.fecha > today) errors.fecha = 'La fecha no puede ser futura';
    if (!form.kilometraje) errors.kilometraje = 'Ingrese kilometraje';
    else {
      const kmNum = Number(form.kilometraje);
      if (isNaN(kmNum) || kmNum < 0) errors.kilometraje = 'Kilometraje inv√°lido';
      else if (editingId !== null && kmNum < originalKilometraje) {
        errors.kilometraje = 'El kilometraje debe ser igual o mayor al √∫ltimo registrado';
      }
    }
    if (!form.proveedor_id) errors.proveedor_id = 'Seleccione proveedor';
    if (!form.costo) errors.costo = 'Ingrese costo';
    else if (isNaN(Number(form.costo)) || Number(form.costo) < 0) errors.costo = 'Costo inv√°lido';

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Manejo cambios inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
    setFormErrors(prev => ({ ...prev, [name]: '' }));
  };

  // Submit formulario (agregar o editar)
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8000/mantenciones/${editingId}`
      : 'http://localhost:8000/mantenciones';

    const res = await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        vehiculo_id: Number(form.vehiculo_id),
        tipo_id: Number(form.tipo_id),
        descripcion: form.descripcion,
        fecha: form.fecha,
        kilometraje: Number(form.kilometraje),
        costo: Number(form.costo),
        proveedor_id: Number(form.proveedor_id),
      }),
    });

    if (res.ok) {
      fetchMantenciones();
      setSuccessMessage(editingId ? '‚úÖ Mantenci√≥n actualizada correctamente' : '‚úÖ Mantenci√≥n agregada correctamente');
      setTimeout(() => setSuccessMessage(''), 3000);
      resetForm();
    } else {
      alert('Error guardando mantenci√≥n');
    }
  };

  // Editar mantenci√≥n: cargar datos en form
  const handleEdit = (m) => {
    setForm({
      vehiculo_id: m.vehiculo_id.toString(),
      tipo_id: m.tipo_id.toString(),
      descripcion: m.descripcion || '',
      fecha: m.fecha,
      kilometraje: m.kilometraje.toString(),
      costo: m.costo.toString(),
      proveedor_id: m.proveedor_id.toString(),
    });
    setEditingId(m.id);
    setOriginalKilometraje(m.kilometraje);
    setFormErrors({});
    setShowForm(true);
  };

  // Eliminar mantenci√≥n
  const handleDelete = async (id) => {
    if (!window.confirm('¬øSeguro que deseas eliminar esta mantenci√≥n?')) return;

    const res = await fetch(`http://localhost:8000/mantenciones/${id}`, {
      method: 'DELETE',
    });

    if (res.ok) {
      fetchMantenciones();
    } else {
      alert('Error eliminando mantenci√≥n');
    }
  };

  const resetForm = () => {
    setForm({
      vehiculo_id: '',
      tipo_id: '',
      descripcion: '',
      fecha: '',
      kilometraje: '',
      costo: '',
      proveedor_id: '',
    });
    setFormErrors({});
    setEditingId(null);
    setOriginalKilometraje(null);
    setShowForm(false);
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">üõ† Gesti√≥n de Mantenciones</h2>

      {successMessage && (
        <div className="mb-4 p-2 bg-green-100 text-green-700 rounded text-center">
          {successMessage}
        </div>
      )}

      {!showForm && (
        <button
          onClick={() => setShowForm(true)}
          className="mb-4 bg-green-600 hover:bg-green-700 text-white px-5 py-2 rounded"
        >
          ‚ûï Agregar nueva mantenci√≥n
        </button>
      )}

      {/* Filtro por fecha */}
      <div className="mb-4 max-w-xs">
        <label className="block mb-1 font-semibold" htmlFor="searchDate">
          Filtrar por fecha:
        </label>
        <input
          type="date"
          id="searchDate"
          value={searchDate}
          onChange={e => setSearchDate(e.target.value)}
          className="border p-2 rounded w-full"
        />
        {searchDate && (
          <button
            onClick={() => setSearchDate('')}
            className="mt-1 text-sm text-blue-600 hover:underline"
            type="button"
          >
            Limpiar filtro
          </button>
        )}
      </div>

      {/* Formulario agregar/editar */}
      {showForm && (
        <form onSubmit={handleSubmit} className="mb-6 bg-white p-6 rounded shadow max-w-4xl mx-auto">
          <h3 className="text-xl font-semibold mb-4">
            {editingId ? '‚úèÔ∏è Editar mantenci√≥n' : '‚ûï Nueva mantenci√≥n'}
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block font-semibold mb-1" htmlFor="vehiculo_id">
                Veh√≠culo <span className="text-red-600">*</span>
              </label>
              <select
                name="vehiculo_id"
                id="vehiculo_id"
                value={form.vehiculo_id}
                onChange={handleChange}
                className={`border p-2 rounded w-full ${formErrors.vehiculo_id ? 'border-red-500' : ''}`}
                disabled={editingId !== null}
              >
                <option value="">-- Seleccione veh√≠culo --</option>
                {vehiculos.map(v => (
                  <option key={v.id} value={v.id}>{v.patente}</option>
                ))}
              </select>
              {formErrors.vehiculo_id && <p className="text-red-500 text-sm mt-1">{formErrors.vehiculo_id}</p>}
            </div>

            <div>
              <label className="block font-semibold mb-1" htmlFor="tipo_id">
                Tipo de mantenci√≥n <span className="text-red-600">*</span>
              </label>
              <select
                name="tipo_id"
                id="tipo_id"
                value={form.tipo_id}
                onChange={handleChange}
                className={`border p-2 rounded w-full ${formErrors.tipo_id ? 'border-red-500' : ''}`}
              >
                <option value="">-- Seleccione tipo --</option>
                {tipos.map(t => (
                  <option key={t.id} value={t.id}>{t.nombre}</option>
                ))}
              </select>
              {formErrors.tipo_id && <p className="text-red-500 text-sm mt-1">{formErrors.tipo_id}</p>}
            </div>

            <div className="md:col-span-2">
              <label className="block font-semibold mb-1" htmlFor="descripcion">
                Descripci√≥n
              </label>
              <textarea
                name="descripcion"
                id="descripcion"
                value={form.descripcion}
                onChange={handleChange}
                rows="3"
                className="border p-2 rounded w-full"
                placeholder="Opcional"
              />
            </div>

            <div>
              <label className="block font-semibold mb-1" htmlFor="fecha">
                Fecha <span className="text-red-600">*</span>
              </label>
              <input
                type="date"
                name="fecha"
                id="fecha"
                value={form.fecha}
                onChange={handleChange}
                className={`border p-2 rounded w-full ${formErrors.fecha ? 'border-red-500' : ''}`}
              />
              {formErrors.fecha && <p className="text-red-500 text-sm mt-1">{formErrors.fecha}</p>}
            </div>

            <div>
              <label className="block font-semibold mb-1" htmlFor="kilometraje">
                Kilometraje (km) <span className="text-red-600">*</span>
              </label>
              <input
                type="number"
                name="kilometraje"
                id="kilometraje"
                min="0"
                value={form.kilometraje}
                onChange={handleChange}
                className={`border p-2 rounded w-full ${formErrors.kilometraje ? 'border-red-500' : ''}`}
              />
              {formErrors.kilometraje && <p className="text-red-500 text-sm mt-1">{formErrors.kilometraje}</p>}
            </div>

            <div>
              <label className="block font-semibold mb-1" htmlFor="costo">
                Costo ($) <span className="text-red-600">*</span>
              </label>
              <input
                type="number"
                name="costo"
                id="costo"
                min="0"
                value={form.costo}
                onChange={handleChange}
                className={`border p-2 rounded w-full ${formErrors.costo ? 'border-red-500' : ''}`}
              />
              {formErrors.costo && <p className="text-red-500 text-sm mt-1">{formErrors.costo}</p>}
            </div>

            <div>
              <label className="block font-semibold mb-1" htmlFor="proveedor_id">
                Proveedor <span className="text-red-600">*</span>
              </label>
              <select
                name="proveedor_id"
                id="proveedor_id"
                value={form.proveedor_id}
                onChange={handleChange}
                className={`border p-2 rounded w-full ${formErrors.proveedor_id ? 'border-red-500' : ''}`}
              >
                <option value="">-- Seleccione proveedor --</option>
                {proveedores.map(p => (
                  <option key={p.id} value={p.id}>{p.nombre}</option>
                ))}
              </select>
              {formErrors.proveedor_id && <p className="text-red-500 text-sm mt-1">{formErrors.proveedor_id}</p>}
            </div>
          </div>

          <div className="mt-6 flex justify-end space-x-3">
            <button
              type="button"
              onClick={resetForm}
              className="px-4 py-2 rounded bg-gray-400 hover:bg-gray-500 text-white"
            >
              Cancelar
            </button>
            <button
              type="submit"
              className="px-5 py-2 rounded bg-blue-600 hover:bg-blue-700 text-white"
            >
              {editingId ? 'Guardar cambios' : 'Agregar mantenci√≥n'}
            </button>
          </div>
        </form>
      )}

      {/* Tabla mantenciones */}
      {filteredMantenciones.length === 0 ? (
        <p className="text-center text-gray-600 mt-8">No hay mantenciones registradas.</p>
      ) : (
        <div className="overflow-x-auto rounded shadow border">
          <table className="w-full table-auto text-left border-collapse">
            <thead className="bg-gray-100">
              <tr>
                <th className="border px-4 py-2">Veh√≠culo</th>
                <th className="border px-4 py-2">Tipo</th>
                <th className="border px-4 py-2">Descripci√≥n</th>
                <th className="border px-4 py-2">Fecha</th>
                <th className="border px-4 py-2">Kilometraje</th>
                <th className="border px-4 py-2">Costo ($)</th>
                <th className="border px-4 py-2">Proveedor</th>
                <th className="border px-4 py-2">Acciones</th>
              </tr>
            </thead>
            <tbody>
              {filteredMantenciones.map(m => {
                const vehiculo = vehiculos.find(v => v.id === m.vehiculo_id);
                const tipo = tipos.find(t => t.id === m.tipo_id);
                const proveedor = proveedores.find(p => p.id === m.proveedor_id);
                return (
                  <tr key={m.id} className="hover:bg-gray-50">
                    <td className="border px-4 py-2">{vehiculo ? vehiculo.patente : 'N/D'}</td>
                    <td className="border px-4 py-2">{tipo ? tipo.nombre : 'N/D'}</td>
                    <td className="border px-4 py-2">{m.descripcion || '-'}</td>
                    <td className="border px-4 py-2">{m.fecha}</td>
                    <td className="border px-4 py-2">{m.kilometraje}</td>
                    <td className="border px-4 py-2">{m.costo}</td>
                    <td className="border px-4 py-2">{proveedor ? proveedor.nombre : 'N/D'}</td>
                    <td className="border px-4 py-2 space-x-2">
                      <button
                        onClick={() => handleEdit(m)}
                        className="text-blue-600 hover:underline"
                        title="Editar"
                      >
                        ‚úèÔ∏è
                      </button>
                      <button
                        onClick={() => handleDelete(m.id)}
                        className="text-red-600 hover:underline"
                        title="Eliminar"
                      >
                        üóëÔ∏è
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Mantenciones;
