import React, { useEffect, useMemo, useState } from 'react';

const Mantenciones = () => {
  const [mantenciones, setMantenciones] = useState([]);
  const [vehiculos, setVehiculos] = useState([]);
  const [tipos, setTipos] = useState([]);
  const [proveedores, setProveedores] = useState([]);

  const [form, setForm] = useState({
    vehiculo_id: '',
    tipo_id: '',
    descripcion: '',
    fecha: '',
    kilometraje: '',
    costo: '',
    proveedor_id: ''
  });

  const [editingId, setEditingId] = useState(null);
  const [formErrors, setFormErrors] = useState({});
  const [searchDate, setSearchDate] = useState('');

  // Fetch data
  const fetchMantenciones = () => {
    fetch('http://localhost:8000/mantenciones')
      .then(res => res.json())
      .then(setMantenciones)
      .catch(err => console.error('Error cargando mantenciones:', err));
  };

  const fetchVehiculos = () => {
    fetch('http://localhost:8000/vehiculos')
      .then(res => res.json())
      .then(setVehiculos);
  };

  const fetchTipos = () => {
    fetch('http://localhost:8000/tipos_mantencion')
      .then(res => res.json())
      .then(setTipos);
  };

  const fetchProveedores = () => {
    fetch('http://localhost:8000/proveedores')
      .then(res => res.json())
      .then(setProveedores);
  };

  useEffect(() => {
    // ✅ Warning eliminado moviendo fetchAll aquí
    const fetchAll = () => {
      fetchMantenciones();
      fetchVehiculos();
      fetchTipos();
      fetchProveedores();
    };
    fetchAll();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
    setFormErrors(prev => ({ ...prev, [name]: '' }));
  };

  const validateForm = () => {
    const errors = {};
    const today = new Date().toISOString().split('T')[0];

    if (!form.vehiculo_id) errors.vehiculo_id = 'Obligatorio';
    if (!form.tipo_id) errors.tipo_id = 'Obligatorio';
    if (!form.fecha) errors.fecha = 'Obligatorio';
    if (form.fecha > today) errors.fecha = 'No puede ser futura';
    if (!form.kilometraje) errors.kilometraje = 'Obligatorio';
    if (!form.costo) errors.costo = 'Obligatorio';
    if (!form.proveedor_id) errors.proveedor_id = 'Obligatorio';

    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8000/mantenciones/${editingId}`
      : 'http://localhost:8000/mantenciones';

    const res = await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    });

    if (res.ok) {
      fetchMantenciones();
      resetForm();
    } else {
      alert('Error al guardar la mantención');
    }
  };

  const handleEdit = (m) => {
    setForm(m);
    setEditingId(m.id);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('¿Eliminar esta mantención?')) return;
    const res = await fetch(`http://localhost:8000/mantenciones/${id}`, {
      method: 'DELETE'
    });
    if (res.ok) {
      fetchMantenciones();
    } else {
      alert('Error al eliminar.');
    }
  };

  const resetForm = () => {
    setForm({
      vehiculo_id: '',
      tipo_id: '',
      descripcion: '',
      fecha: '',
      kilometraje: '',
      costo: '',
      proveedor_id: ''
    });
    setFormErrors({});
    setEditingId(null);
  };

  const filteredMantenciones = useMemo(() => {
    if (!searchDate) return mantenciones;
    return mantenciones.filter(m => m.fecha === searchDate);
  }, [mantenciones, searchDate]);

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h2 className="text-2xl font-bold mb-6">🛠 Mantenciones</h2>

      {/* Buscar por fecha */}
      <div className="mb-4">
        <label className="mr-2 font-medium">Buscar por fecha:</label>
        <input
          type="date"
          value={searchDate}
          onChange={(e) => setSearchDate(e.target.value)}
          className="border p-2 rounded"
        />
        {searchDate && (
          <button
            onClick={() => setSearchDate('')}
            className="ml-2 text-sm text-red-500 hover:underline"
          >
            Limpiar
          </button>
        )}
      </div>

      {/* Formulario */}
      <form onSubmit={handleSubmit} className="bg-white shadow rounded p-4 mb-6">
        <h3 className="text-lg font-semibold mb-4">
          {editingId ? '✏️ Editar Mantención' : '➕ Nueva Mantención'}
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label>Vehículo</label>
            <select
              name="vehiculo_id"
              value={form.vehiculo_id}
              onChange={handleChange}
              className="w-full border p-2 rounded"
            >
              <option value="">Selecciona</option>
              {vehiculos.map(v => (
                <option key={v.id} value={v.id}>{v.patente}</option>
              ))}
            </select>
            {formErrors.vehiculo_id && <p className="text-red-500 text-sm">{formErrors.vehiculo_id}</p>}
          </div>

          <div>
            <label>Tipo</label>
            <select
              name="tipo_id"
              value={form.tipo_id}
              onChange={handleChange}
              className="w-full border p-2 rounded"
            >
              <option value="">Selecciona</option>
              {tipos.map(t => (
                <option key={t.id} value={t.id}>{t.nombre}</option>
              ))}
            </select>
            {formErrors.tipo_id && <p className="text-red-500 text-sm">{formErrors.tipo_id}</p>}
          </div>

          <div>
            <label>Fecha</label>
            <input
              type="date"
              name="fecha"
              value={form.fecha}
              onChange={handleChange}
              className="w-full border p-2 rounded"
            />
            {formErrors.fecha && <p className="text-red-500 text-sm">{formErrors.fecha}</p>}
          </div>

          <div>
            <label>Descripción</label>
            <input
              type="text"
              name="descripcion"
              value={form.descripcion}
              onChange={handleChange}
              className="w-full border p-2 rounded"
            />
          </div>

          <div>
            <label>Kilometraje</label>
            <input
              type="number"
              name="kilometraje"
              value={form.kilometraje}
              onChange={handleChange}
              className="w-full border p-2 rounded"
            />
            {formErrors.kilometraje && <p className="text-red-500 text-sm">{formErrors.kilometraje}</p>}
          </div>

          <div>
            <label>Costo</label>
            <input
              type="number"
              name="costo"
              value={form.costo}
              onChange={handleChange}
              className="w-full border p-2 rounded"
            />
            {formErrors.costo && <p className="text-red-500 text-sm">{formErrors.costo}</p>}
          </div>

          <div>
            <label>Proveedor</label>
            <select
              name="proveedor_id"
              value={form.proveedor_id}
              onChange={handleChange}
              className="w-full border p-2 rounded"
            >
              <option value="">Selecciona</option>
              {proveedores.map(p => (
                <option key={p.id} value={p.id}>{p.nombre}</option>
              ))}
            </select>
            {formErrors.proveedor_id && <p className="text-red-500 text-sm">{formErrors.proveedor_id}</p>}
          </div>
        </div>

        <div className="mt-4 flex justify-end gap-2">
          <button
            type="button"
            onClick={resetForm}
            className="bg-gray-400 hover:bg-gray-500 text-white px-4 py-2 rounded"
          >
            Cancelar
          </button>
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
          >
            {editingId ? 'Guardar Cambios' : 'Agregar Mantención'}
          </button>
        </div>
      </form>

      {/* Tabla */}
      {filteredMantenciones.length === 0 ? (
        <p className="text-center text-gray-500 mt-6">No hay mantenciones registradas.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full table-auto border rounded shadow bg-white">
            <thead className="bg-gray-100">
              <tr>
                <th className="p-3 border">Vehículo</th>
                <th className="p-3 border">Tipo</th>
                <th className="p-3 border">Fecha</th>
                <th className="p-3 border">Descripción</th>
                <th className="p-3 border">Km</th>
                <th className="p-3 border">Costo</th>
                <th className="p-3 border">Proveedor</th>
                <th className="p-3 border text-center">Acciones</th>
              </tr>
            </thead>
            <tbody>
              {filteredMantenciones.map(m => {
                const v = vehiculos.find(v => v.id === m.vehiculo_id);
                const t = tipos.find(t => t.id === m.tipo_id);
                const p = proveedores.find(p => p.id === m.proveedor_id);
                return (
                  <tr key={m.id} className="hover:bg-gray-50">
                    <td className="p-3 border">{v?.patente || 'N/D'}</td>
                    <td className="p-3 border">{t?.nombre || 'N/D'}</td>
                    <td className="p-3 border">{m.fecha}</td>
                    <td className="p-3 border">{m.descripcion || '-'}</td>
                    <td className="p-3 border">{m.kilometraje}</td>
                    <td className="p-3 border">${m.costo}</td>
                    <td className="p-3 border">{p?.nombre || 'N/D'}</td>
                    <td className="p-3 border text-center space-x-2">
                      <button
                        onClick={() => handleEdit(m)}
                        className="text-blue-600 hover:underline"
                      >
                        ✏️
                      </button>
                      <button
                        onClick={() => handleDelete(m.id)}
                        className="text-red-600 hover:underline"
                      >
                        🗑️
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Mantenciones;
