import React from 'react';

const App = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-white to-green-100 flex flex-col items-center justify-center px-4">
      <div className="max-w-5xl w-full text-center">
        <h1 className="text-4xl font-bold text-gray-800 mb-4">🚗 Sistema de Mantención Vehicular</h1>
        <p className="text-gray-600 mb-10">Accede rápidamente a todos los módulos del sistema</p>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          <Card title="Gestión de Vehículos" description="Agrega, edita o elimina vehículos" />
          <Card title="Registro de Mantenciones" description="Agrega y consulta mantenciones" />
          <Card title="Programación" description="Programa mantenciones futuras" />
          <Card title="Historial y Reportes" description="Revisa historial y genera reportes" />
          <Card title="Gestión de Proveedores" description="Registra y administra talleres o proveedores" />
        </div>
      </div>
    </div>
  );
};

const Card = ({ title, description }) => (
  <div className="bg-white shadow-md hover:shadow-lg transition duration-300 rounded-xl p-6 border border-gray-200">
    <h2 className="text-xl font-semibold text-gray-800 mb-2">{title}</h2>
    <p className="text-gray-500">{description}</p>
    <button className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
      Ir al módulo
    </button>
  </div>
);

export default App;
