import React, { useEffect, useState } from 'react';

const Reportes = () => {
  const [reportes, setReportes] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/reportes/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        fecha_desde: null,
        fecha_hasta: null,
        patente: null,
        tipo_mantencion: null,
        proveedor: null
      })
    })
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setReportes(data);
        } else {
          console.warn('Respuesta inesperada:', data);
          setReportes([]);
        }
      })
      .catch(err => console.error('Error cargando reportes:', err));
  }, []);

  return (
    <div className="p-6 w-full max-w-5xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">üìä Historial y Reportes</h2>

      {reportes.length === 0 ? (
        <p>No hay reportes disponibles.</p>
      ) : (
        <ul className="space-y-2">
          {reportes.map((r) => (
            <li key={r.id} className="bg-white p-4 rounded shadow text-left">
              <p><strong>üöó Patente:</strong> {r.vehiculoPatente}</p>
              <p><strong>üîß Tipo:</strong> {r.tipoMantencion}</p>
              <p><strong>üè≠ Proveedor:</strong> {r.proveedor}</p>
              <p><strong>üìÖ Fecha:</strong> {r.fecha}</p>
              <p><strong>üí∞ Costo:</strong> ${r.costo}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Reportes;
