import React, { useEffect, useState } from 'react';

const Mantenciones = () => {
  const [mantenciones, setMantenciones] = useState([]);
  const [vehiculos, setVehiculos] = useState([]);
  const [form, setForm] = useState({ id: null, vehiculoId: '', descripcion: '', fecha: '' });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');

  // Carga mantenciones desde el backend
  const fetchMantenciones = () => {
    fetch('http://localhost:8000/mantenciones/')
      .then(res => res.json())
      .then(data => setMantenciones(data))
      .catch(err => console.error('Error cargando mantenciones:', err));
  };

  // Carga vehículos para el select
  const fetchVehiculos = () => {
    fetch('http://localhost:8000/vehiculos/')
      .then(res => res.json())
      .then(data => setVehiculos(data))
      .catch(err => console.error('Error cargando vehículos:', err));
  };

  useEffect(() => {
    fetchMantenciones();
    fetchVehiculos();
  }, []);

  // Cambios en el formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setErrors(prev => ({ ...prev, [name]: '' }));
    setForm(prev => ({ ...prev, [name]: value }));
  };

  // Validar formulario simple
  const validate = () => {
    const newErrors = {};
    if (!form.vehiculoId) newErrors.vehiculoId = 'Seleccione un vehículo';
    if (!form.descripcion) newErrors.descripcion = 'Ingrese descripción';
    if (!form.fecha) newErrors.fecha = 'Ingrese fecha';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Crear o editar mantención
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    const method = form.id ? 'PUT' : 'POST';
    const url = form.id
      ? `http://localhost:8000/mantenciones/${form.id}`
      : 'http://localhost:8000/mantenciones/';

    const body = {
      vehiculoId: form.vehiculoId,
      descripcion: form.descripcion,
      fecha: form.fecha,
    };

    const res = await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(body),
    });

    if (res.ok) {
      setSuccessMessage(form.id ? 'Mantención actualizada' : 'Mantención creada');
      setTimeout(() => setSuccessMessage(''), 3000);
      setForm({ id: null, vehiculoId: '', descripcion: '', fecha: '' });
      fetchMantenciones();
    } else {
      alert('Error al guardar mantención');
    }
  };

  // Cargar mantención para editar
  const handleEdit = (mantencion) => {
    setForm({
      id: mantencion.id,
      vehiculoId: mantencion.vehiculoId,
      descripcion: mantencion.descripcion,
      fecha: mantencion.fecha,
    });
    setErrors({});
  };

  // Eliminar mantención
  const handleDelete = async (id) => {
    if (!window.confirm('¿Eliminar esta mantención?')) return;
    const res = await fetch(`http://localhost:8000/mantenciones/${id}`, { method: 'DELETE' });
    if (res.ok) {
      fetchMantenciones();
    } else {
      alert('Error al eliminar mantención');
    }
  };

  // Cancelar edición
  const handleCancel = () => {
    setForm({ id: null, vehiculoId: '', descripcion: '', fecha: '' });
    setErrors({});
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">🛠 Gestión de Mantenciones</h2>

      {successMessage && (
        <div className="mb-4 p-2 bg-green-100 text-green-700 rounded">{successMessage}</div>
      )}

      <form onSubmit={handleSubmit} className="mb-6 bg-white p-4 rounded shadow">
        <div className="mb-3">
          <label className="block font-semibold mb-1">Vehículo</label>
          <select
            name="vehiculoId"
            value={form.vehiculoId}
            onChange={handleChange}
            className={`w-full p-2 border rounded ${errors.vehiculoId ? 'border-red-500' : ''}`}
          >
            <option value="">-- Seleccione un vehículo --</option>
            {vehiculos.map(v => (
              <option key={v.id} value={v.id}>
                {v.patente} - {v.marca} {v.modelo}
              </option>
            ))}
          </select>
          {errors.vehiculoId && <p className="text-red-500 text-sm">{errors.vehiculoId}</p>}
        </div>

        <div className="mb-3">
          <label className="block font-semibold mb-1">Descripción</label>
          <input
            type="text"
            name="descripcion"
            value={form.descripcion}
            onChange={handleChange}
            className={`w-full p-2 border rounded ${errors.descripcion ? 'border-red-500' : ''}`}
            placeholder="Descripción de la mantención"
          />
          {errors.descripcion && <p className="text-red-500 text-sm">{errors.descripcion}</p>}
        </div>

        <div className="mb-3">
          <label className="block font-semibold mb-1">Fecha</label>
          <input
            type="date"
            name="fecha"
            value={form.fecha}
            onChange={handleChange}
            className={`w-full p-2 border rounded ${errors.fecha ? 'border-red-500' : ''}`}
          />
          {errors.fecha && <p className="text-red-500 text-sm">{errors.fecha}</p>}
        </div>

        <div className="flex space-x-2 justify-end">
          {form.id && (
            <button
              type="button"
              onClick={handleCancel}
              className="px-4 py-2 bg-gray-400 text-white rounded hover:bg-gray-500"
            >
              Cancelar
            </button>
          )}
          <button
            type="submit"
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            {form.id ? 'Actualizar' : 'Crear'}
          </button>
        </div>
      </form>

      <h3 className="text-xl font-semibold mb-2">Mantenciones Registradas</h3>

      {mantenciones.length === 0 ? (
        <p className="text-gray-600">No hay mantenciones registradas.</p>
      ) : (
        <table className="w-full border-collapse border border-gray-300 rounded shadow">
          <thead className="bg-gray-100">
            <tr>
              <th className="border border-gray-300 p-2 text-left">Vehículo</th>
              <th className="border border-gray-300 p-2 text-left">Descripción</th>
              <th className="border border-gray-300 p-2 text-left">Fecha</th>
              <th className="border border-gray-300 p-2 text-center">Acciones</th>
            </tr>
          </thead>
          <tbody>
            {mantenciones.map(m => {
              const vehiculo = vehiculos.find(v => v.id === m.vehiculoId);
              return (
                <tr key={m.id} className="hover:bg-gray-50">
                  <td className="border border-gray-300 p-2">{vehiculo ? vehiculo.patente : 'Desconocido'}</td>
                  <td className="border border-gray-300 p-2">{m.descripcion}</td>
                  <td className="border border-gray-300 p-2">{m.fecha}</td>
                  <td className="border border-gray-300 p-2 text-center space-x-2">
                    <button
                      onClick={() => handleEdit(m)}
                      className="bg-yellow-400 text-white px-3 py-1 rounded hover:bg-yellow-500"
                    >
                      Editar
                    </button>
                    <button
                      onClick={() => handleDelete(m.id)}
                      className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                    >
                      Eliminar
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Mantenciones;
