import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Proveedores = () => {
  const [proveedores, setProveedores] = useState([]);
  const [direcciones, setDirecciones] = useState([]);
  const [regiones, setRegiones] = useState([]);      // regiones del gobierno
  const [comunas, setComunas] = useState([]);        // comunas filtradas por región
  const [search, setSearch] = useState('');
  const [form, setForm] = useState({
    nombre: '',
    telefono: '',
    email: '',
    direccion_id: '',
    usarNuevaDireccion: false,
    nuevaCalle: '',
    nuevaRegionCode: '',
    nuevaComunaNombre: '',
  });
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Cargar proveedores y direcciones existentes
  const fetchProveedores = async () => {
    try {
      const res = await fetch('http://localhost:8000/proveedores/');
      const data = await res.json();
      setProveedores(data);
    } catch (err) {
      console.error('Error cargando proveedores:', err);
    }
  };

  const fetchDirecciones = async () => {
    try {
      const res = await fetch('http://localhost:8000/direcciones/');
      const data = await res.json();
      setDirecciones(data);
    } catch (err) {
      console.error('Error cargando direcciones:', err);
    }
  };

  // Cargar todas las regiones desde la API DPA
  const fetchRegiones = async () => {
    try {
      const res = await fetch('https://apis.modernizacion.cl/dpa/regiones');
      const data = await res.json();
      setRegiones(data);
    } catch (err) {
      console.error('Error cargando regiones:', err);
    }
  };

  // Cuando seleccionas una región, cargar sus comunas
  const fetchComunasPorRegion = async (regionCodigo) => {
    try {
      const res = await fetch(`https://apis.modernizacion.cl/dpa/regiones/${regionCodigo}/comunas`);
      const data = await res.json();
      setComunas(data);
    } catch (err) {
      console.error('Error cargando comunas:', err);
    }
  };

  useEffect(() => {
    fetchProveedores();
    fetchDirecciones();
    fetchRegiones();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      setForm(prev => ({ ...prev, [name]: checked }));
    } else {
      setForm(prev => ({ ...prev, [name]: value }));
      if (name === 'nuevaRegionCode') {
        // cuando cambias región nueva, limpiar comuna y recargar comunas
        setForm(prev => ({ ...prev, nuevaComunaNombre: '' }));
        fetchComunasPorRegion(value);
      }
    }
    setError(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Validaciones
    if (!form.nombre || !form.telefono || !form.email) {
      setError('Por favor completa nombre, teléfono y correo.');
      return;
    }
    if (form.usarNuevaDireccion) {
      if (!form.nuevaCalle || !form.nuevaRegionCode || !form.nuevaComunaNombre) {
        setError('Por favor completa todos los datos de la nueva dirección.');
        return;
      }
    } else {
      if (!form.direccion_id) {
        setError('Selecciona una dirección existente o crea una nueva.');
        return;
      }
    }
    // Validar nombre duplicado
    const nombreLower = form.nombre.trim().toLowerCase();
    const nombreExiste = proveedores.some(p =>
      p.nombre.trim().toLowerCase() === nombreLower &&
      p.id !== editingId
    );
    if (nombreExiste) {
      setError('Ya existe un proveedor con ese nombre.');
      return;
    }

    // Preparar los datos para enviar
    let body = {
      nombre: form.nombre,
      telefono: form.telefono,
      email: form.email,
    };
    if (form.usarNuevaDireccion) {
      body = {
        ...body,
        direccion_nueva: {
          calle: form.nuevaCalle,
          region: form.nuevaRegionCode,
          comuna: form.nuevaComunaNombre
        }
      };
    } else {
      body = {
        ...body,
        direccion_id: form.direccion_id
      };
    }

    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8000/proveedores/${editingId}`
      : 'http://localhost:8000/proveedores/';

    try {
      const res = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body),
      });
      if (res.ok) {
        fetchProveedores();
        // limpiar formulario
        setForm({
          nombre: '',
          telefono: '',
          email: '',
          direccion_id: '',
          usarNuevaDireccion: false,
          nuevaCalle: '',
          nuevaRegionCode: '',
          nuevaComunaNombre: '',
        });
        setEditingId(null);
        setError(null);
      } else {
        const data = await res.json();
        setError(data.message || 'Error al guardar proveedor');
      }
    } catch (err) {
      console.error(err);
      setError('Error de conexión al guardar proveedor');
    }
  };


  const filteredProveedores = proveedores.filter(p =>
    p.nombre.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="p-6 w-full max-w-5xl mx-auto relative">
      <button
        onClick={() => navigate(-1)}
        className="absolute top-4 left-4 bg-gray-400 hover:bg-gray-500 text-white px-3 py-2 rounded"
      >
        ← Volver al inicio
      </button>

      <h2 className="text-2xl font-bold mb-6 text-center">🏭 Proveedores</h2>

      <div className="relative mb-4">
        <input
          type="text"
          placeholder="🔍 Buscar por nombre..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="p-2 pl-10 border rounded w-full"
        />
      </div>

      <form onSubmit={handleSubmit} className="mb-6 bg-white p-4 rounded shadow">
        <h3 className="text-lg font-semibold mb-2">
          {editingId ? 'Editar proveedor' : 'Agregar nuevo proveedor'}
        </h3>

        {error && <p className="text-red-600 mb-2">{error}</p>}

        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
          <input
            type="text"
            name="nombre"
            placeholder="Nombre *"
            value={form.nombre}
            onChange={handleChange}
            required
            className="border p-2 rounded"
          />
          <input
            type="text"
            name="telefono"
            placeholder="Teléfono *"
            value={form.telefono}
            onChange={handleChange}
            required
            className="border p-2 rounded"
          />
          <input
            type="email"
            name="email"
            placeholder="Email *"
            value={form.email}
            onChange={handleChange}
            required
            className="border p-2 rounded"
          />

          {/* Dirección existente */}
          {!form.usarNuevaDireccion && (
            <select
              name="direccion_id"
              value={form.direccion_id}
              onChange={handleChange}
              required={!form.usarNuevaDireccion}
              className="border p-2 rounded"
            >
              <option value="">Seleccione dirección existente</option>
              {direcciones.map(d => (
                <option key={d.id} value={d.id}>
                  {`${d.calle}, ${d.comuna}, ${d.region}`}
                </option>
              ))}
            </select>
          )}

          <div className="flex items-center">
            <input
              type="checkbox"
              name="usarNuevaDireccion"
              checked={form.usarNuevaDireccion}
              onChange={handleChange}
              className="mr-2"
            />
            <label>Crear nueva dirección</label>
          </div>
        </div>

        {form.usarNuevaDireccion && (
          <div className="mt-4 bg-gray-100 p-4 rounded">
            <h4 className="font-medium mb-2">Datos nueva dirección</h4>
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              <input
                type="text"
                name="nuevaCalle"
                placeholder="Calle *"
                value={form.nuevaCalle}
                onChange={handleChange}
                required={form.usarNuevaDireccion}
                className="border p-2 rounded"
              />
              <select
                name="nuevaRegionCode"
                value={form.nuevaRegionCode}
                onChange={handleChange}
                required={form.usarNuevaDireccion}
                className="border p-2 rounded"
              >
                <option value="">Seleccione Región *</option>
                {regiones.map(r => (
                  <option key={r.codigo} value={r.codigo}>
                    {r.nombre}
                  </option>
                ))}
              </select>
              <select
                name="nuevaComunaNombre"
                value={form.nuevaComunaNombre}
                onChange={handleChange}
                required={form.usarNuevaDireccion}
                className="border p-2 rounded"
              >
                <option value="">Seleccione Comuna *</option>
                {comunas.map(c => (
                  <option key={c.codigo} value={c.nombre}>
                    {c.nombre}
                  </option>
                ))}
              </select>
            </div>
          </div>
        )}

        <button
          type="submit"
          className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          {editingId ? 'Guardar cambios' : 'Agregar proveedor'}
        </button>
      </form>

      {filteredProveedores.length === 0 ? (
        <p>No hay proveedores registrados.</p>
      ) : (
        <ul className="space-y-2">
          {filteredProveedores.map(p => (
            <li
              key={p.id}
              className="bg-white p-4 rounded shadow flex justify-between items-center"
            >
              <div>
                <strong>{p.nombre}</strong>
                <br />
                📞 {p.telefono}
                <br />
                ✉️ {p.email}
                <br />
                📍{' '}
                {p.direccion ? (
                  <em>{`${p.direccion.calle}, ${p.direccion.comuna}, ${p.direccion.region}`}</em>
                ) : (
                  <em style={{ color: 'red' }}>Dirección no disponible</em>
                )}
              </div>
              <div className="space-x-2">
                <button
                  onClick={() => handleEdit(p)}
                  className="bg-yellow-400 text-white px-3 py-1 rounded hover:bg-yellow-500"
                >
                  Editar
                </button>
                <button
                  onClick={() => handleDelete(p.id)}
                  className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                >
                  Eliminar
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Proveedores;
