import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // import useNavigate para botón volver atrás

const Proveedores = () => {
  const [proveedores, setProveedores] = useState([]);
  const [search, setSearch] = useState('');
  const [form, setForm] = useState({ nombre: '', telefono: '', email: '', direccion: '' });
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const fetchProveedores = () => {
    fetch('http://localhost:8000/proveedores/')
      .then(res => res.json())
      .then(data => setProveedores(data))
      .catch(err => console.error('Error cargando proveedores:', err));
  };

  useEffect(() => {
    fetchProveedores();
  }, []);

  const filteredProveedores = proveedores.filter(p =>
    p.nombre.toLowerCase().includes(search.toLowerCase())
  );

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setError(null); // limpiar error al escribir
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.nombre || !form.telefono || !form.email) {
      setError('Por favor completa todos los campos obligatorios.');
      return;
    }

    const nombreExiste = proveedores.some(p =>
      p.nombre.toLowerCase() === form.nombre.trim().toLowerCase() &&
      p.id !== editingId
    );

    if (nombreExiste) {
      setError('Ya existe un proveedor con ese nombre.');
      return;
    }

    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8000/proveedores/${editingId}`
      : 'http://localhost:8000/proveedores/';

    try {
      const res = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });

      if (res.ok) {
        fetchProveedores();
        setForm({ nombre: '', telefono: '', email: '', direccion: '' });
        setEditingId(null);
        setError(null);
      } else {
        const data = await res.json();
        setError(data.message || 'Error al guardar proveedor');
      }
    } catch (error) {
      setError('Error de conexión al guardar proveedor');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('¿Estás seguro de eliminar este proveedor?')) return;

    try {
      const res = await fetch(`http://localhost:8000/proveedores/${id}`, {
        method: 'DELETE',
      });

      if (res.ok) {
        fetchProveedores();
      } else {
        const data = await res.json();
        if (data.code === 'DEPENDENCY_ERROR') {
          alert('No se puede eliminar el proveedor porque tiene mantenciones activas.');
        } else {
          alert('Error al eliminar proveedor');
        }
      }
    } catch (error) {
      alert('Error de conexión al eliminar proveedor');
    }
  };

  const handleEdit = (proveedor) => {
    setForm({
      nombre: proveedor.nombre,
      telefono: proveedor.telefono,
      email: proveedor.email,
      direccion: proveedor.direccion || '',
    });
    setEditingId(proveedor.id);
    setError(null);
  };

  return (
    <div className="p-6 w-full max-w-4xl mx-auto">

      <h2 className="text-2xl font-bold mb-4">🏭 Proveedores</h2>

      <input
        type="text"
        placeholder="Buscar por nombre..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="mb-4 p-2 border rounded w-full"
      />

      <button
        onClick={() => navigate(-1)}
        className="mb-4 bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded"
      >
        ← Volver al inicio
      </button>

      <form onSubmit={handleSubmit} className="mb-6 bg-white p-4 rounded shadow">
        <h3 className="text-lg font-semibold mb-2">{editingId ? 'Editar proveedor' : 'Agregar nuevo proveedor'}</h3>

        {error && <p className="text-red-600 mb-2">{error}</p>}

        <div className="grid grid-cols-1 sm:grid-cols-4 gap-4">
          <input
            type="text"
            name="nombre"
            placeholder="Nombre *"
            value={form.nombre}
            onChange={handleChange}
            className="border p-2 rounded"
            required
          />
          <input
            type="text"
            name="telefono"
            placeholder="Teléfono *"
            value={form.telefono}
            onChange={handleChange}
            className="border p-2 rounded"
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email *"
            value={form.email}
            onChange={handleChange}
            className="border p-2 rounded"
            required
          />
          <input
            type="text"
            name="direccion"
            placeholder="Dirección"
            value={form.direccion}
            onChange={handleChange}
            className="border p-2 rounded"
          />
        </div>

        <button type="submit" className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          {editingId ? 'Guardar cambios' : 'Agregar proveedor'}
        </button>
      </form>

      {filteredProveedores.length === 0 ? (
        <p>No hay proveedores registrados.</p>
      ) : (
        <ul className="space-y-2">
          {filteredProveedores.map(p => (
            <li key={p.id} className="bg-white p-4 rounded shadow flex justify-between items-center">
              <div>
                <strong>{p.nombre}</strong> - {p.telefono} - {p.email}
                {p.direccion && ` - ${p.direccion}`}
              </div>
              <div className="space-x-2">
                <button
                  onClick={() => handleEdit(p)}
                  className="bg-yellow-400 text-white px-3 py-1 rounded hover:bg-yellow-500"
                >
                  Editar
                </button>
                <button
                  onClick={() => handleDelete(p.id)}
                  className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                >
                  Eliminar
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Proveedores;
