import React, { useEffect, useState } from 'react';

const RegionesComunasForm = () => {
  const [regiones, setRegiones] = useState([]);
  const [comunas, setComunas] = useState([]);
  const [regionSeleccionada, setRegionSeleccionada] = useState('');
  const [comunaSeleccionada, setComunaSeleccionada] = useState('');

  // Obtener regiones al cargar el componente
  useEffect(() => {
    const fetchRegiones = async () => {
      console.log("üîÑ Cargando regiones...");
      try {
        const res = await fetch('https://apis.digital.gob.cl/dpa/regiones');
        const data = await res.json();
        console.log("‚úÖ Regiones cargadas:", data);
        setRegiones(data);
      } catch (err) {
        console.error("‚ùå Error al cargar regiones:", err);
      }
    };

    fetchRegiones();
  }, []);

  // Obtener comunas al cambiar regi√≥n
  useEffect(() => {
    const fetchComunas = async () => {
      if (!regionSeleccionada) {
        setComunas([]);
        return;
      }

      console.log("üîÑ Cargando comunas para regi√≥n:", regionSeleccionada);
      try {
        const res = await fetch(
          `https://apis.digital.gob.cl/dpa/regiones/${regionSeleccionada}/comunas`
        );
        const data = await res.json();
        console.log("‚úÖ Comunas cargadas:", data);
        setComunas(data);
      } catch (err) {
        console.error("‚ùå Error al cargar comunas:", err);
        setComunas([]);
      }
    };

    fetchComunas();
  }, [regionSeleccionada]);

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-xl font-bold mb-4">üåé Selecci√≥n de Regi√≥n y Comuna</h2>

      <div className="mb-4">
        <label className="block mb-1">Regi√≥n</label>
        <select
          value={regionSeleccionada}
          onChange={(e) => {
            setRegionSeleccionada(e.target.value);
            setComunaSeleccionada('');
          }}
          className="border p-2 rounded w-full"
        >
          <option value="">Seleccione una regi√≥n</option>
          {regiones.map((region) => (
            <option key={region.codigo} value={region.codigo}>
              {region.nombre}
            </option>
          ))}
        </select>
      </div>

      <div>
        <label className="block mb-1">Comuna</label>
        <select
          value={comunaSeleccionada}
          onChange={(e) => setComunaSeleccionada(e.target.value)}
          className="border p-2 rounded w-full"
          disabled={!comunas.length}
        >
          <option value="">Seleccione una comuna</option>
          {comunas.map((comuna) => (
            <option key={comuna.codigo} value={comuna.nombre}>
              {comuna.nombre}
            </option>
          ))}
        </select>
      </div>

      {/* Resultado */}
      <div className="mt-6 bg-gray-100 p-4 rounded">
        <h4 className="font-semibold mb-2">üìã Selecci√≥n actual</h4>
        <p>Regi√≥n: <strong>{regionSeleccionada || 'No seleccionada'}</strong></p>
        <p>Comuna: <strong>{comunaSeleccionada || 'No seleccionada'}</strong></p>
      </div>
    </div>
  );
};

export default RegionesComunasForm;
