import React, { useEffect, useState, useCallback } from 'react';

const Reportes = () => {
  const [fechaDesde, setFechaDesde] = useState('');
  const [fechaHasta, setFechaHasta] = useState('');
  const [patente, setPatente] = useState('');
  const [tipoMantencion, setTipoMantencion] = useState('');
  const [proveedor, setProveedor] = useState('');

  const [reportes, setReportes] = useState([]);
  const [graficoUrl, setGraficoUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const filtros = {
    fecha_desde: fechaDesde || null,
    fecha_hasta: fechaHasta || null,
    patente: patente.trim() || null,
    tipo_mantencion: tipoMantencion || null,
    proveedor: proveedor.trim() || null,
  };

  const fetchReportes = useCallback(async () => {
    setLoading(true);
    setError(null);
    setGraficoUrl(null);
    try {
      const res = await fetch('http://localhost:8000/reportes/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(filtros),
      });

      if (!res.ok) throw new Error(`Error HTTP: ${res.status}`);

      const data = await res.json();

      if (Array.isArray(data)) {
        setReportes(data);
      } else {
        setReportes([]);
        setError('Respuesta inesperada del servidor.');
      }
    } catch (err) {
      setError('Error cargando reportes: ' + err.message);
    } finally {
      setLoading(false);
    }
  }, [filtros]);

  useEffect(() => {
    fetchReportes();
  }, [fetchReportes]);

  const handleDescargarExcel = async () => {
    setError(null);
    try {
      const url = new URL('http://localhost:8000/reportes/');
      url.searchParams.append('exportar_excel', 'true');

      const res = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(filtros),
      });

      if (!res.ok) throw new Error(`Error HTTP: ${res.status}`);

      const blob = await res.blob();
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.download = 'reporte_mantenciones.xlsx';
      link.click();
      link.remove();
    } catch (err) {
      setError('Error al descargar Excel: ' + err.message);
    }
  };

  const handleVerGrafico = async () => {
    setError(null);
    try {
      const url = new URL('http://localhost:8000/reportes/');
      url.searchParams.append('generar_grafico', 'true');

      const res = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(filtros),
      });

      if (!res.ok) throw new Error(`Error HTTP: ${res.status}`);

      const blob = await res.blob();
      const imageUrl = URL.createObjectURL(blob);

      if (graficoUrl) URL.revokeObjectURL(graficoUrl);
      setGraficoUrl(imageUrl);

      // Descargar la imagen autom치ticamente
      const link = document.createElement('a');
      link.href = imageUrl;
      link.download = 'grafico_mantenciones.png';
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      setError('Error al generar gr치fico: ' + err.message);
    }
  };

  const formatFecha = (fechaStr) => {
    if (!fechaStr) return '-';
    const d = new Date(fechaStr);
    return d.toLocaleDateString();
  };

  const formatCosto = (costo) => {
    if (costo == null) return '-';
    return costo.toLocaleString('es-CL', {
      style: 'currency',
      currency: 'CLP',
    });
  };

  return (
    <div className="p-6 w-full max-w-5xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">游늵 Historial y Reportes</h2>

      {/* Filtros */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <div>
          <label className="block font-semibold mb-1" htmlFor="fechaDesde">Fecha desde:</label>
          <input
            id="fechaDesde"
            type="date"
            value={fechaDesde}
            onChange={(e) => setFechaDesde(e.target.value)}
            className="border rounded px-2 py-1 w-full"
            max={fechaHasta || undefined}
          />
        </div>

        <div>
          <label className="block font-semibold mb-1" htmlFor="fechaHasta">Fecha hasta:</label>
          <input
            id="fechaHasta"
            type="date"
            value={fechaHasta}
            onChange={(e) => setFechaHasta(e.target.value)}
            className="border rounded px-2 py-1 w-full"
            min={fechaDesde || undefined}
          />
        </div>

        <div>
          <label className="block font-semibold mb-1" htmlFor="patente">Patente:</label>
          <input
            id="patente"
            type="text"
            value={patente}
            onChange={(e) => setPatente(e.target.value.toUpperCase())}
            className="border rounded px-2 py-1 w-full"
            placeholder="Ej: ABC123"
            maxLength={7}
          />
        </div>

        <div>
          <label className="block font-semibold mb-1" htmlFor="tipoMantencion">Tipo mantenci칩n:</label>
          <select
            id="tipoMantencion"
            value={tipoMantencion}
            onChange={(e) => setTipoMantencion(e.target.value)}
            className="border rounded px-2 py-1 w-full"
          >
            <option value="">-- Todos --</option>
            <option value="preventiva">Preventiva</option>
            <option value="correctiva">Correctiva</option>
          </select>
        </div>

        <div>
          <label className="block font-semibold mb-1" htmlFor="proveedor">Proveedor:</label>
          <input
            id="proveedor"
            type="text"
            value={proveedor}
            onChange={(e) => setProveedor(e.target.value)}
            className="border rounded px-2 py-1 w-full"
            placeholder="Nombre proveedor"
          />
        </div>
      </div>

      {/* Botones */}
      <div className="mb-6 space-x-4">
        <button
          disabled={loading}
          onClick={fetchReportes}
          className={`px-4 py-2 rounded text-white ${loading ? 'bg-gray-400' : 'bg-indigo-600 hover:bg-indigo-700'}`}
        >
          游댃 Actualizar Reportes
        </button>

        <button
          disabled={loading}
          onClick={handleDescargarExcel}
          className={`px-4 py-2 rounded text-white ${loading ? 'bg-gray-400' : 'bg-green-600 hover:bg-green-700'}`}
        >
          游닌 Descargar Excel
        </button>

        <button
          disabled={loading}
          onClick={handleVerGrafico}
          className={`px-4 py-2 rounded text-white ${loading ? 'bg-gray-400' : 'bg-blue-500 hover:bg-blue-700'}`}
        >
          游늳 Ver y Descargar Gr치fico
        </button>
      </div>

      {error && <p className="text-red-600 mb-4">{error}</p>}

      {graficoUrl && (
        <div className="mb-6">
          <h3 className="text-lg font-semibold mb-2">Gr치fico de Costos</h3>
          <img
            src={graficoUrl}
            alt="Gr치fico de costos por tipo de mantenci칩n"
            className="rounded shadow max-w-full h-auto"
          />
        </div>
      )}

      {reportes.length === 0 && !loading ? (
        <p>No hay reportes disponibles.</p>
      ) : (
        <ul className="space-y-2">
          {reportes.map((r) => (
            <li key={r.id} className="bg-white p-4 rounded shadow text-left">
              <p><strong>游뚱 Patente:</strong> {r.vehiculoPatente}</p>
              <p><strong>游댢 Tipo:</strong> {r.tipoMantencion}</p>
              <p><strong>游낈 Proveedor:</strong> {r.proveedor}</p>
              <p><strong>游늰 Fecha:</strong> {formatFecha(r.fecha)}</p>
              <p><strong>游눯 Costo:</strong> {formatCosto(r.costo)}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Reportes;
