import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Puedes pegar este JSON de regiones/comunas o cargarlo desde archivo
const regionesYComunas = [
  {
    region: "Región Metropolitana",
    comunas: ["Santiago", "Providencia", "Maipú", "La Florida", "Puente Alto"]
  },
  {
    region: "Valparaíso",
    comunas: ["Valparaíso", "Viña del Mar", "Quilpué", "Villa Alemana", "San Antonio"]
  },
  // agrega las demás regiones/comunas que necesites...
];

const Proveedores = () => {
  const [proveedores, setProveedores] = useState([]);
  const [direcciones, setDirecciones] = useState([]);
  const [search, setSearch] = useState('');
  const [form, setForm] = useState({
    nombre: '',
    telefono: '',
    email: '',
    // campos para dirección nueva opcional
    usarNuevaDireccion: false,
    nuevaCalle: '',
    nuevaRegion: '',
    nuevaComuna: '',
    direccion_id: '',  // si selecciones una dirección existente
  });
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const fetchProveedores = () => {
    fetch('http://localhost:8000/proveedores/')
      .then(res => res.json())
      .then(data => setProveedores(data))
      .catch(err => console.error('Error cargando proveedores:', err));
  };

  const fetchDirecciones = () => {
    fetch('http://localhost:8000/direcciones/')
      .then(res => res.json())
      .then(data => setDirecciones(data))
      .catch(err => console.error('Error cargando direcciones:', err));
  };

  useEffect(() => {
    fetchProveedores();
    fetchDirecciones();
  }, []);

  const filteredProveedores = proveedores.filter(p =>
    p.nombre.toLowerCase().includes(search.toLowerCase())
  );

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      setForm({ ...form, [name]: checked });
    } else {
      setForm({ ...form, [name]: value });
    }
    setError(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Validaciones
    if (!form.nombre || !form.telefono || !form.email) {
      setError('Por favor completa nombre, teléfono y correo.');
      return;
    }
    if (form.usarNuevaDireccion) {
      if (!form.nuevaCalle || !form.nuevaRegion || !form.nuevaComuna) {
        setError('Por favor completa todos los datos de la nueva dirección.');
        return;
      }
    } else {
      if (!form.direccion_id) {
        setError('Selecciona una dirección o crea una nueva.');
        return;
      }
    }

    // Validar nombre duplicado
    const nombreLower = form.nombre.trim().toLowerCase();
    const nombreExiste = proveedores.some(p =>
      p.nombre.trim().toLowerCase() === nombreLower &&
      p.id !== editingId
    );
    if (nombreExiste) {
      setError('Ya existe un proveedor con ese nombre.');
      return;
    }

    // Preparar body
    let body = {
      nombre: form.nombre,
      telefono: form.telefono,
      email: form.email,
    };

    if (form.usarNuevaDireccion) {
      body = {
        ...body,
        nueva_direccion: {
          calle: form.nuevaCalle,
          comuna: form.nuevaComuna,
          region: form.nuevaRegion
        }
      };
    } else {
      body = {
        ...body,
        direccion_id: form.direccion_id
      };
    }

    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8000/proveedores/${editingId}`
      : 'http://localhost:8000/proveedores/';

    try {
      const res = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body),
      });

      if (res.ok) {
        fetchProveedores();
        // resetear formulario
        setForm({
          nombre: '',
          telefono: '',
          email: '',
          usarNuevaDireccion: false,
          nuevaCalle: '',
          nuevaRegion: '',
          nuevaComuna: '',
          direccion_id: ''
        });
        setEditingId(null);
        setError(null);
      } else {
        const data = await res.json();
        setError(data.message || 'Error al guardar proveedor');
      }
    } catch (error) {
      setError('Error de conexión al guardar proveedor');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('¿Estás seguro de eliminar este proveedor?')) return;

    try {
      const res = await fetch(`http://localhost:8000/proveedores/${id}`, {
        method: 'DELETE',
      });

      if (res.ok) {
        fetchProveedores();
      } else {
        const data = await res.json();
        if (data.code === 'DEPENDENCY_ERROR') {
          alert('No se puede eliminar el proveedor porque tiene mantenciones activas.');
        } else {
          alert('Error al eliminar proveedor');
        }
      }
    } catch (error) {
      alert('Error de conexión al eliminar proveedor');
    }
  };

  const handleEdit = (proveedor) => {
    setForm({
      nombre: proveedor.nombre,
      telefono: proveedor.telefono,
      email: proveedor.email,
      usarNuevaDireccion: false,
      nuevaCalle: '',
      nuevaRegion: '',
      nuevaComuna: '',
      direccion_id: proveedor.direccion?.id || '',
    });
    setEditingId(proveedor.id);
    setError(null);
  };

  // Comunas disponibles según región seleccionada
  const comunasFiltradas = form.nuevaRegion
    ? (regionesYComunas.find(r => r.region === form.nuevaRegion)?.comunas || [])
    : [];

  return (
    <div className="p-6 w-full max-w-5xl mx-auto relative">
      {/* Botón Volver */}
      <button
        onClick={() => navigate(-1)}
        className="absolute top-4 left-2 bg-gray-400 hover:bg-gray-500 text-white px-2 py-2 rounded"
      >
        ← Volver al inicio
      </button>

      <h2 className="text-2xl font-bold mb-6 text-center">🏭 Proveedores</h2>

      {/* Búsqueda con lupa */}
      <div className="relative mb-4">
        <input
          type="text"
          placeholder="🔍 Buscar por nombre..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="p-2 pl-10 border rounded w-full"
        />
      </div>

      <form onSubmit={handleSubmit} className="mb-6 bg-white p-4 rounded shadow">
        <h3 className="text-lg font-semibold mb-2">
          {editingId ? 'Editar proveedor' : 'Agregar nuevo proveedor'}
        </h3>

        {error && <p className="text-red-600 mb-2">{error}</p>}

        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
          <input
            type="text"
            name="nombre"
            placeholder="Nombre *"
            value={form.nombre}
            onChange={handleChange}
            className="border p-2 rounded"
            required
          />
          <input
            type="text"
            name="telefono"
            placeholder="Teléfono *"
            value={form.telefono}
            onChange={handleChange}
            className="border p-2 rounded"
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email *"
            value={form.email}
            onChange={handleChange}
            className="border p-2 rounded"
            required
          />

          {/* Selección de dirección existente */}
          {!form.usarNuevaDireccion && (
            <select
              name="direccion_id"
              value={form.direccion_id}
              onChange={handleChange}
              className="border p-2 rounded"
              required={!form.usarNuevaDireccion}
            >
              <option value="">Seleccione una dirección existente</option>
              {direcciones.map(d => (
                <option key={d.id} value={d.id}>
                  {`${d.calle}, ${d.comuna}, ${d.region}`}
                </option>
              ))}
            </select>
          )}

          {/* Checkbox para usar nueva dirección */}
          <div className="flex items-center">
            <input
              type="checkbox"
              name="usarNuevaDireccion"
              checked={form.usarNuevaDireccion}
              onChange={handleChange}
              className="mr-2"
            />
            <label>Crear nueva dirección</label>
          </div>
        </div>

        {/* Sub-formulario para nueva dirección */}
        {form.usarNuevaDireccion && (
          <div className="mt-4 bg-gray-100 p-4 rounded">
            <h4 className="font-medium mb-2">Datos nueva dirección</h4>
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              <input
                type="text"
                name="nuevaCalle"
                placeholder="Calle *"
                value={form.nuevaCalle}
                onChange={handleChange}
                className="border p-2 rounded"
                required={form.usarNuevaDireccion}
              />
              <select
                name="nuevaRegion"
                value={form.nuevaRegion}
                onChange={handleChange}
                className="border p-2 rounded"
                required={form.usarNuevaDireccion}
              >
                <option value="">Seleccione Región *</option>
                {regionesYComunas.map(r => (
                  <option key={r.region} value={r.region}>
                    {r.region}
                  </option>
                ))}
              </select>
              <select
                name="nuevaComuna"
                value={form.nuevaComuna}
                onChange={handleChange}
                className="border p-2 rounded"
                required={form.usarNuevaDireccion}
              >
                <option value="">Seleccione Comuna *</option>
                {comunasFiltradas.map(c => (
                  <option key={c} value={c}>
                    {c}
                  </option>
                ))}
              </select>
            </div>
          </div>
        )}

        <button
          type="submit"
          className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          {editingId ? 'Guardar cambios' : 'Agregar proveedor'}
        </button>
      </form>

      {filteredProveedores.length === 0 ? (
        <p>No hay proveedores registrados.</p>
      ) : (
        <ul className="space-y-2">
          {filteredProveedores.map(p => (
            <li
              key={p.id}
              className="bg-white p-4 rounded shadow flex justify-between items-center"
            >
              <div>
                <strong>{p.nombre}</strong>
                <br />
                📞 {p.telefono}
                <br />
                ✉️ {p.email}
                <br />
                📍{' '}
                {p.direccion ? (
                  <em>{`${p.direccion.calle}, ${p.direccion.comuna}, ${p.direccion.region}`}</em>
                ) : (
                  <em style={{ color: 'red' }}>Dirección no disponible</em>
                )}
              </div>
              <div className="space-x-2">
                <button
                  onClick={() => handleEdit(p)}
                  className="bg-yellow-400 text-white px-3 py-1 rounded hover:bg-yellow-500"
                >
                  Editar
                </button>
                <button
                  onClick={() => handleDelete(p.id)}
                  className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                >
                  Eliminar
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Proveedores;
