import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Necesitas react-router-dom para navegación

const Mantenciones = () => {
  const [mantenciones, setMantenciones] = useState([]);
  const [vehiculos, setVehiculos] = useState([]);
  const [tipos, setTipos] = useState([]);
  const [proveedores, setProveedores] = useState([]);

  const [form, setForm] = useState({
    vehiculo_id: '',
    tipo_id: '',
    descripcion: '',
    fecha: '',
    kilometraje: '',
    costo: '',
    proveedor_id: ''
  });

  const [editingId, setEditingId] = useState(null);
  const [formErrors, setFormErrors] = useState({});
  const [searchDate, setSearchDate] = useState('');
  const [searchTipoId, setSearchTipoId] = useState(''); // Nuevo filtro por tipo

  const [showForm, setShowForm] = useState(false); // Estado para controlar la visibilidad del formulario

  const navigate = useNavigate(); // Hook para navegación

  useEffect(() => {
    fetch('http://localhost:8000/mantenciones')
      .then(res => res.json())
      .then(setMantenciones)
      .catch(err => {
        console.error('Error fetching mantenciones:', err);
        setMantenciones([]);
      });

    fetch('http://localhost:8000/vehiculos')
      .then(res => res.json())
      .then(setVehiculos)
      .catch(err => {
        console.error('Error fetching vehiculos:', err);
        setVehiculos([]);
      });

    fetch('http://localhost:8000/tipos_mantencion')
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setTipos(data);
        } else if (Array.isArray(data.tipos)) {
          // Si backend envía { tipos: [...] }
          setTipos(data.tipos);
        } else {
          console.error('Tipos mantencion no es un array:', data);
          setTipos([]);
        }
      })
      .catch(err => {
        console.error('Error fetching tipos mantencion:', err);
        setTipos([]);
      });

    fetch('http://localhost:8000/proveedores')
      .then(res => res.json())
      .then(setProveedores)
      .catch(err => {
        console.error('Error fetching proveedores:', err);
        setProveedores([]);
      });
  }, []);

  // Manejo inputs formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
    setFormErrors(prev => ({ ...prev, [name]: '' }));
  };

  // Validar formulario
  const validateForm = () => {
    const errors = {};
    const today = new Date().toISOString().split('T')[0];

    if (!form.vehiculo_id) errors.vehiculo_id = 'Obligatorio';
    if (!form.tipo_id) errors.tipo_id = 'Obligatorio';
    if (!form.fecha) errors.fecha = 'Obligatorio';
    else if (form.fecha > today) errors.fecha = 'La fecha no puede ser futura';
    if (!form.kilometraje) errors.kilometraje = 'Obligatorio';
    else if (isNaN(Number(form.kilometraje)) || Number(form.kilometraje) < 0) errors.kilometraje = 'Debe ser número positivo';

    if (form.vehiculo_id && form.kilometraje) {
      const kmNum = Number(form.kilometraje);
      const maxKm = Math.max(
        ...mantenciones
          .filter(m => m.vehiculo_id.toString() === form.vehiculo_id)
          .map(m => m.kilometraje),
        0
      );
      if (kmNum < maxKm) {
        errors.kilometraje = `Debe ser mayor o igual al último registrado (${maxKm})`;
      }
    }

    if (!form.costo) errors.costo = 'Obligatorio';
    else if (isNaN(Number(form.costo)) || Number(form.costo) < 0) errors.costo = 'Debe ser número positivo';

    if (!form.proveedor_id) errors.proveedor_id = 'Obligatorio';

    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    if (!window.confirm(editingId ? "¿Confirma guardar los cambios?" : "¿Confirma registrar la mantención?")) return;

    const payload = {
      ...form,
      vehiculo_id: Number(form.vehiculo_id),
      tipo_id: Number(form.tipo_id),
      proveedor_id: Number(form.proveedor_id),
      kilometraje: Number(form.kilometraje),
      costo: Number(form.costo)
    };

    try {
      const res = await fetch(
        editingId
          ? `http://localhost:8000/mantenciones/${editingId}`
          : 'http://localhost:8000/mantenciones',
        {
          method: editingId ? 'PUT' : 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload)
        }
      );

      if (res.ok) {
        const data = await res.json();
        const updatedMantenciones = editingId
          ? mantenciones.map(m => (m.id === editingId ? data : m))
          : [...mantenciones, data];
        setMantenciones(updatedMantenciones);
        resetForm();
        setShowForm(false); // Ocultar formulario después de guardar
      } else {
        alert('Error al guardar mantención');
      }
    } catch {
      alert('Error de conexión con el servidor');
    }
  };

  const handleEdit = (m) => {
    setForm({
      vehiculo_id: m.vehiculo_id.toString(),
      tipo_id: m.tipo_id.toString(),
      descripcion: m.descripcion || '',
      fecha: m.fecha,
      kilometraje: m.kilometraje.toString(),
      costo: m.costo.toString(),
      proveedor_id: m.proveedor_id.toString()
    });
    setEditingId(m.id);
    setFormErrors({});
  };

  const resetForm = () => {
    setForm({
      vehiculo_id: '',
      tipo_id: '',
      descripcion: '',
      fecha: '',
      kilometraje: '',
      costo: '',
      proveedor_id: ''
    });
    setEditingId(null);
    setFormErrors({});
  };

  // Filtrado combinado por fecha y tipo
  const filteredMantenciones = useMemo(() => {
    return mantenciones.filter(m => {
      const matchFecha = searchDate ? m.fecha === searchDate : true;
      const matchTipo = searchTipoId ? m.tipo_id.toString() === searchTipoId : true;
      return matchFecha && matchTipo;
    });
  }, [mantenciones, searchDate, searchTipoId]);

  return (
    <div className="p-6 w-full max-w-5xl mx-auto relative">
      <button
        onClick={() => navigate(-1)}
        className="absolute top-4 left-4 bg-gray-400 hover:bg-gray-500 text-white px-3 py-2 rounded"
      >
        ← Volver al inicio
      </button>

      <h2 className="text-2xl font-bold mb-6 text-center">Mantenciones</h2>

      {/* Mensaje de éxito */}
      {successMessage && (
        <div className="mb-4 p-2 bg-green-100 text-green-700 rounded text-center">
          {successMessage}
        </div>
      )}

      {/* Botón para mostrar el formulario */}
      {!showForm && (
        <button
          onClick={() => setShowForm(true)} // Mostrar formulario al hacer clic
          className="mb-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          ➕ Agregar nueva Mantención
        </button>
      )}

      {/* Filtros */}
      <div className="mb-6 flex flex-wrap items-center space-x-4">
        {/* Filtro por fecha */}
        <label className="font-semibold">Filtrar por fecha:</label>
        <input
          type="date"
          value={searchDate}
          onChange={e => setSearchDate(e.target.value)}
          className="border rounded p-2"
        />
        {searchDate && (
          <button
            onClick={() => setSearchDate('')}
            className="text-red-600 underline"
          >
            Limpiar filtro
          </button>
        )}

        {/* Filtro por tipo */}
        <label className="font-semibold ml-6">Filtrar por tipo:</label>
        <select
          value={searchTipoId}
          onChange={e => setSearchTipoId(e.target.value)}
          className="border rounded p-2"
        >
          <option value="">Todos</option>
          {tipos.map(t => (
            <option key={t.id} value={t.id}>{t.nombre}</option>
          ))}
        </select>
        {searchTipoId && (
          <button
            onClick={() => setSearchTipoId('')}
            className="text-red-600 underline ml-2"
          >
            Limpiar filtro
          </button>
        )}
      </div>

      {/* FORMULARIO */}
      {showForm && (
        <form onSubmit={handleSubmit} className="mb-10 p-4 border rounded shadow">
          <h2 className="text-xl font-semibold mb-4">{editingId ? 'Editar mantención' : 'Registrar mantención'}</h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {/* Vehículo */}
            <div>
              <label className="block font-semibold mb-1">Vehículo *</label>
              <select
                name="vehiculo_id"
                value={form.vehiculo_id}
                onChange={handleChange}
                className="border rounded w-full p-2"
              >
                <option value="">Seleccione vehículo</option>
                {vehiculos.map(v => (
                  <option key={v.id} value={v.id}>{v.marca} {v.modelo} ({v.año})</option>
                ))}
              </select>
              {formErrors.vehiculo_id && <p className="text-red-600">{formErrors.vehiculo_id}</p>}
            </div>

            {/* Tipo mantención */}
            <div>
              <label className="block font-semibold mb-1">Tipo mantención *</label>
              <select
                name="tipo_id"
                value={form.tipo_id}
                onChange={handleChange}
                className="border rounded w-full p-2"
              >
                <option value="">Seleccione tipo</option>
                {tipos.map(t => (
                  <option key={t.id} value={t.id}>{t.nombre}</option>
                ))}
              </select>
              {formErrors.tipo_id && <p className="text-red-600">{formErrors.tipo_id}</p>}
            </div>

            {/* Fecha */}
            <div>
              <label className="block font-semibold mb-1">Fecha *</label>
              <input
                type="date"
                name="fecha"
                value={form.fecha}
                onChange={handleChange}
                className="border rounded w-full p-2"
              />
              {formErrors.fecha && <p className="text-red-600">{formErrors.fecha}</p>}
            </div>

            {/* Kilometraje */}
            <div>
              <label className="block font-semibold mb-1">Kilometraje *</label>
              <input
                type="number"
                name="kilometraje"
                value={form.kilometraje}
                onChange={handleChange}
                className="border rounded w-full p-2"
                min="0"
              />
              {formErrors.kilometraje && <p className="text-red-600">{formErrors.kilometraje}</p>}
            </div>

            {/* Costo */}
            <div>
              <label className="block font-semibold mb-1">Costo *</label>
              <input
                type="number"
                name="costo"
                value={form.costo}
                onChange={handleChange}
                className="border rounded w-full p-2"
                min="0"
                step="0.01"
              />
              {formErrors.costo && <p className="text-red-600">{formErrors.costo}</p>}
            </div>

            {/* Proveedor */}
            <div>
              <label className="block font-semibold mb-1">Proveedor *</label>
              <select
                name="proveedor_id"
                value={form.proveedor_id}
                onChange={handleChange}
                className="border rounded w-full p-2"
              >
                <option value="">Seleccione proveedor</option>
                {proveedores.map(p => (
                  <option key={p.id} value={p.id}>{p.nombre}</option>
                ))}
              </select>
              {formErrors.proveedor_id && <p className="text-red-600">{formErrors.proveedor_id}</p>}
            </div>

            {/* Descripción (opcional) */}
            <div className="md:col-span-3">
              <label className="block font-semibold mb-1">Descripción</label>
              <textarea
                name="descripcion"
                value={form.descripcion}
                onChange={handleChange}
                className="border rounded w-full p-2"
                rows="3"
              />
            </div>
          </div>

          <div className="mt-4 flex space-x-4">
            <button type="submit" className="bg-blue-700 text-white px-4 py-2 rounded hover:bg-blue-800">
              {editingId ? 'Guardar cambios' : 'Registrar'}
            </button>
            {editingId && (
              <button
                type="button"
                onClick={resetForm}
                className="bg-gray-400 text-black px-4 py-2 rounded hover:bg-gray-500"
              >
                Cancelar
              </button>
            )}
          </div>
        </form>
      )}

      {/* Lista de mantenciones */}
      <div>
        <h2 className="text-2xl font-semibold mb-4">Lista de mantenciones</h2>

        {filteredMantenciones.length === 0 ? (
          <p>No hay mantenciones que mostrar.</p>
        ) : (
          <table className="w-full table-auto border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-200">
                <th className="border border-gray-300 px-4 py-2">Vehículo</th>
                <th className="border border-gray-300 px-4 py-2">Tipo</th>
                <th className="border border-gray-300 px-4 py-2">Fecha</th>
                <th className="border border-gray-300 px-4 py-2">Kilometraje</th>
                <th className="border border-gray-300 px-4 py-2">Costo</th>
                <th className="border border-gray-300 px-4 py-2">Proveedor</th>
                <th className="border border-gray-300 px-4 py-2">Descripción</th>
                <th className="border border-gray-300 px-4 py-2">Acciones</th>
              </tr>
            </thead>
            <tbody>
              {filteredMantenciones.map(m => {
                const vehiculo = vehiculos.find(v => v.id === m.vehiculo_id);
                const tipo = tipos.find(t => t.id === m.tipo_id);
                const proveedor = proveedores.find(p => p.id === m.proveedor_id);

                return (
                  <tr key={m.id} className="hover:bg-gray-100">
                    <td className="border border-gray-300 px-4 py-2">{vehiculo ? `${vehiculo.marca} ${vehiculo.modelo}` : 'N/D'}</td>
                    <td className="border border-gray-300 px-4 py-2">{tipo ? tipo.nombre : 'N/D'}</td>
                    <td className="border border-gray-300 px-4 py-2">{m.fecha}</td>
                    <td className="border border-gray-300 px-4 py-2">{m.kilometraje}</td>
                    <td className="border border-gray-300 px-4 py-2">${m.costo}</td>
                    <td className="border border-gray-300 px-4 py-2">{proveedor ? proveedor.nombre : 'N/D'}</td>
                    <td className="border border-gray-300 px-4 py-2">{m.descripcion || '-'}</td>
                    <td className="border border-gray-300 px-4 py-2">
                      <button
                        onClick={() => handleEdit(m)}
                        className="bg-yellow-400 text-black px-2 py-1 rounded hover:bg-yellow-500"
                      >
                        Editar
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Mantenciones;
