import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Vehiculos = () => {
  const [vehicles, setVehicles] = useState([]);
  const [search, setSearch] = useState('');
  const [form, setForm] = useState({ patente: '', marca: '', modelo: '' });
  const [editingId, setEditingId] = useState(null);

  // Obtener veh√≠culos
  const fetchVehicles = () => {
    fetch('http://localhost:8000/vehiculos/')
      .then(res => res.json())
      .then(data => setVehicles(data))
      .catch(err => console.error('Error cargando veh√≠culos:', err));
  };

  useEffect(() => {
    fetchVehicles();
  }, []);

  // Filtrar por patente
  const filteredVehicles = vehicles.filter(vehicle =>
    vehicle.patente.toLowerCase().includes(search.toLowerCase())
  );

  // Manejar cambio en el formulario
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Crear o editar veh√≠culo
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.patente || !form.marca || !form.modelo) return;

    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8000/vehiculos/${editingId}`
      : 'http://localhost:8000/vehiculos/';

    const res = await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    });

    if (res.ok) {
      fetchVehicles();
      setForm({ patente: '', marca: '', modelo: '' });
      setEditingId(null);
    } else {
      console.error('Error al guardar veh√≠culo');
    }
  };

  // Eliminar veh√≠culo
  const handleDelete = async (id) => {
    if (!window.confirm('¬øEst√°s seguro de eliminar este veh√≠culo?')) return;

    const res = await fetch(`http://localhost:8000/vehiculos/${id}`, {
      method: 'DELETE',
    });

    if (res.ok) {
      fetchVehicles();
    } else {
      console.error('Error al eliminar veh√≠culo');
    }
  };

  // Cargar veh√≠culo al formulario para edici√≥n
  const handleEdit = (vehiculo) => {
    setForm({
      patente: vehiculo.patente,
      marca: vehiculo.marca,
      modelo: vehiculo.modelo,
    });
    setEditingId(vehiculo.id);
  };

  return (
    <div className="p-6 w-full max-w-5xl mx-auto relative">
      {/* Bot√≥n Volver */}
      <button
        onClick={() => navigate(-1)}
        className="absolute top-4 left-2 bg-gray-400 hover:bg-gray-500 text-white px-2 py-2 rounded"
      >
        ‚Üê Volver al inicio
      </button>
      
      <h2 className="text-2xl font-bold mb-4">üöó Veh√≠culos</h2>

       {/* B√∫squeda con √≠cono de lupa */}
      <div className="relative mb-4">
        <input
          type="text"
          placeholder="Buscar por nombre..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="p-2 pl-10 border rounded w-full"
        />
        <span className="absolute left-3 top-2.5 text-gray-500">üîç</span>
      </div>

      {/* Formulario */}
      <form onSubmit={handleSubmit} className="mb-6 bg-white p-4 rounded shadow">
        <h3 className="text-lg font-semibold mb-2">{editingId ? 'Editar veh√≠culo' : 'Agregar nuevo veh√≠culo'}</h3>
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
          <input
            type="text"
            name="patente"
            placeholder="Patente"
            value={form.patente}
            onChange={handleChange}
            className="border p-2 rounded"
          />
          <input
            type="text"
            name="marca"
            placeholder="Marca"
            value={form.marca}
            onChange={handleChange}
            className="border p-2 rounded"
          />
          <input
            type="text"
            name="modelo"
            placeholder="Modelo"
            value={form.modelo}
            onChange={handleChange}
            className="border p-2 rounded"
          />
        </div>
        <button type="submit" className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          {editingId ? 'Guardar cambios' : 'Agregar veh√≠culo'}
        </button>
      </form>

      {/* Lista de veh√≠culos */}
      {filteredVehicles.length === 0 ? (
        <p>No hay veh√≠culos registrados.</p>
      ) : (
        <ul className="space-y-2">
          {filteredVehicles.map(vehicle => (
            <li key={vehicle.id} className="bg-white p-4 rounded shadow flex justify-between items-center">
              <div>
                <strong>{vehicle.patente}</strong> - {vehicle.modelo} - {vehicle.marca}
              </div>
              <div className="space-x-2">
                <button
                  onClick={() => handleEdit(vehicle)}
                  className="bg-yellow-400 text-white px-3 py-1 rounded hover:bg-yellow-500"
                >
                  Editar
                </button>
                <button
                  onClick={() => handleDelete(vehicle.id)}
                  className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                >
                  Eliminar
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Vehiculos;
